						mbtn_mod_auth_OnPressed()
{
	var strFKManager = _swdoc.oCust().fk_manager;
	//if the current customer has a manager
	if(strFKManager!="")
	{
		//see if the manager is already in the authoriser list
		var intRow = app.g.sqllist_findrow_byvalue(mainform.sl_wfauth, strFKManager, "fk_auth_id");
		if(intRow>-1)
		{
			//if they are in the authoriser list already, do not pass details to On Fly Authoriser form
			strFKManager = "";
		}
	}
	//--
	//-- allow analyst to add authorisers - can only add analysts and the customers manager
	var strURL = "fk_callref=" + _swdoc.OC().callref + "&fk_workflow_id=" + _swdoc.OC().bpm_workflow_id + "&fk_stage_id=" + _swdoc.OC().bpm_stage_id + "&unanimous="+_swdoc.oStage().flg_unanimous_auth + "&fk_manager="+strFKManager;
	app.g.popup("bpm_add_ocauth",strURL,function()
	{
		_swdoc.MF().sl_wfauth.Refresh(); 
	});

}

