						TabDet_OnTabSelected(nTab)
{
	var tProgress=3;
	var tTasks = 2;
	// DTH: webclient
	var tAuth = 1;
	if(nTab == tProgress)
	{
		mainform.SetTimer(1001, 10);
	}
	else if(nTab == tTasks)
	{
		_swdoc.boolRefreshTasks = true;
		// DTH: webclient
		app._slf(_swdoc.MF().sl_tasks, "cr="+ _swdoc.OC().callref +"&sid="+ app.pfu(_swdoc.OC().bpm_stage_id));
		_swdoc.MF().sl_tasks.Refresh();
	}
	// DTH: webclient
	else if(nTab == tAuth)
	{
		app._slf(_swdoc.MF().sl_wfauth, "cr="+ _swdoc.OC().callref +"&sid="+ app.pfu(_swdoc.OC().bpm_stage_id));
		_swdoc.MF().sl_wfauth.Refresh();
	}
}

