						mb_cr_actions_OnMenuItem(strName, nItemID)
{
	
	var iRelease = 2;
	var iChange = 3;
	var iChangeCompany = 5;
	
	if(nItemID==iRelease)
	{
		//-- RJC 2.4.0 FR
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.opencall;
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = _swdoc.opencall.prob_text;
		app.g.LOG_CALL_FOR_PK_SERVICE = _swdoc.OC().itsm_fk_service;

		//-- open new problem record
		app.global.LogNewCall("Release Request");
    }else if(nItemID==iChange)
	{
		//-- RJC 2.4.0 FR
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.opencall;
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = _swdoc.opencall.prob_text;
		app.g.LOG_CALL_FOR_PK_SERVICE = _swdoc.OC().itsm_fk_service;
		
		//-- open new problem record
		app.global.LogNewCall("Change Request");
    }
    
}

