						sl_configitems1_OnItemSelected(nRow)
{
	//-- NWJ
	//-- 27.11.2006
	//-- enable list menus if an item is selected	
	var boolActive = (sl_configitems1.rowCount() > 0);
	var selCount = app.g.sl_getselected_count(sl_configitems1);	
	app._een(mbtn_cci_views1,(selCount==1));	
	app._een(mbtn_cci_actions1,boolActive);
	
}

						TabRel_OnTabSelected(nTab)
{
//-- NWJ - 28.11.2006
	//-- Handle tab clicking
	tCCIs = 0;
	tIncidents = 1;
	tRFCs = 2;
	tRELs = 3;
	tSRs = 4;	
	tTasks = 5;
	tOrgs = 6;
	
	if(nTab==tCCIs)
	{
		if (sl_configitems1.rowCount() < 1) _swdoc.EF().sl_configitems1.Refresh();
	}
	else if(nTab==tRFCs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_rfcs.rowCount() < 1) _swdoc.EF().sl_rfcs.Refresh();
	}
	else if(nTab==tSRs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_srs.rowCount() < 1) _swdoc.EF().sl_srs.Refresh();
	}
	else if(nTab==tRELs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_rels1.rowCount() < 1) _swdoc.EF().sl_rels1.Refresh();
	}	
	else if(nTab==tIncidents)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_incs1.rowCount() < 1) _swdoc.EF().sl_incs1.Refresh();
	}
	else if(nTab==tTasks)
	{
		if (sl_taskhistory1.rowCount() < 1)
		{
			_swdoc.EF().sl_taskhistory1.Refresh();
			sl_taskhistory1.SetRowSelected(0);
		}
	}
	else if(nTab==tOrgs)
	{
		//-- load affected orgs
		_swdoc.filter_impact_orgs(_swdoc.EF()); 
		_swdoc.EF().sl_orgimpact.Refresh();
	}
		
}

