						mbtn_rel_views1_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	
	if(nItemID == iDetails)
	{
		if(sl_rels1.curSel > -1)sl_rels1.EditRecord(sl_rels1.curSel);
	}
}

						mbtn_rel_actions1_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	
	//-- NWJ - unlink release
	if(nItemID == iDisassociate)
	{
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_rels1,0,",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs,_swdoc.OC().callref,'RELEASE-REQUEST'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This service request has been dis-associated from the following release requests\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Release Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRELText = "The service request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this release request";
					app.g.new_diaryevent(arrCallrefs[x], strRELText,"Service Request Management","Service Request Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Service Request Management","Release Dis-association");
			}
		
			_swdoc.EF().sl_rels1.Refresh();
			sl_rels1_OnItemSelected(0);
		}	
	}

}

						btn_srch_rel1_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	var strURL = "canreset=1&fk_company_id=" + app.pfu(_swdoc.oCust().fk_company_id);  

    app.itsm.search_calls("REL",strURL,true,function(oResults)
    {
    	 if(oResults.selectedkeys=="") return;
   
		var array_rels = oResults.selectedkeys.split(",");  
		var strText = "This service request has been associated with the following release requests\n\n";
		var boolRELs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_rels.length;x++)
    	{
        	var rel_callref = array_rels[x];
        	var rel_callref_text = app.g.pad_callref(rel_callref);
			
			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_rels1,rel_callref,0);
			if (rowPos == -1)
			{ 		
				boolRELs=true;
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(rel_callref,_swdoc.OC().callref,'RELEASE-REQUEST'))
				{
					strText += "Release Request (" + rel_callref_text + ") associated\n";
					
					//-- update rfc diary
					strRelText = "The service request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this release.";
					app.g.new_diaryevent(rel_callref,strRelText,"Service Request Management","Release Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolRELs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Service Request Management","Release Association");
			_swdoc.EF().sl_rels1.Refresh();
		}
    });
}

