			{
				app.g.unrelate_master_calls(strCallRefs, _swdoc.OC().callref,  'REQUEST-REQUEST');
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This service request has been dis-associated from the following service requests\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Service Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRELText = "The service request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this service request";
					app.g.new_diaryevent(arrCallrefs[x], strRELText,"Service Request Management","Service Request Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Service Request Management","Service Request Dis-association");
			}
		
			_swdoc.EF().sl_srs.Refresh();
			sl_srs_OnItemSelected(0);
		}	
	}

}

						btn_srch_kes1_OnPressed()
{
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	var strURL = "canreset=1";  

    app.itsm.search_calls("SR",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;
   
		var array_srs = oResults.selectedkeys.split(",");  
		var strText = "This service request has been associated with the following service requests\n\n";
		var boolSRs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_srs.length;x++)
    	{
        	var sr_callref = array_srs[x];
        	var sr_callref_text = app.g.pad_callref(sr_callref);
			
			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_srs,sr_callref,0);
			if (rowPos == -1)
			{ 		
				boolSRs=true;
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(_swdoc.OC().callref,sr_callref,'REQUEST-REQUEST'))
				{
					app.g.relate_calls(sr_callref,_swdoc.OC().callref,'REQUEST-REQUEST');
					strText += "Service Request (" + sr_callref_text + ") associated\n";
					
					//-- update rfc diary
					strRelText = "The Service Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this Service Request.";
					app.g.new_diaryevent(sr_callref,strRelText,"Service Request Management","Service Request Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolSRs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Service Request Management","Service Request Association");
			_swdoc.EF().sl_srs.Refresh();
		}
    });
}

