						mbtn_inc_views1_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	var iCust = 2
	if(nItemID == iDetails)
	{
		if(sl_incs1.curSel > -1)sl_incs1.EditRecord(sl_incs1.curSel);
	}
	else if (nItemID==iCust)
	{
		if(sl_incs1.curSel > -1)
		{
			var strKeySearch = app.g.sl_getcolvalue(sl_incs1,-1,"cust_id");
			if(strKeySearch!="")app.OpenFormForEdit("tfCustomer", strKeySearch, "", true);
		}
	}
	
}

						mbtn_inc_actions1_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;
	var iCauseCI = 2;
	var iImpactCI = 3;

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	//-- NWJ - unlink incident
	if(nItemID == iDisassociate)
	{
		
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_incs1,0,",");
		if(strCallRefs!= "")
		{
			_swdoc.remove_change_incidents(strCallRefs,false);
		}	
	}
	else if(nItemID == iImpactCI)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_incs1,0,",");	
		var oResultCIs = app.cmdb.get_call_ci_info(strCallRefs , "INCIDENT", "");
		_swdoc.link_affect_cis_to_change(oResultCIs.keys,oResultCIs.text);
	}
	else if(nItemID == iCauseCI)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_incs1,0,",");	
		var oResultCIs = app.cmdb.get_call_ci_info(strCallRefs , "INCIDENT", "");
		_swdoc.link_cause_cis_to_change(oResultCIs.keys,oResultCIs.text);
	}	
	

}

