method.code:
							OnFormLoaded()
{
	app._slf(sl_hist, "swc=fk_callref&kvs=" + _swdoc.GetArg("callref"));
	app._slf(sl_audit, "swc=fk_callref&kvs=" + _swdoc.GetArg("callref"));
	sl_hist_OnItemSelected(0);
	app._etab(TabHist,0);
	TabHist_OnTabSelected(0);
	
}

							sl_hist_OnItemSelected(nRow)
{
	//-- 
	if((sl_hist.rowCount()>0)&&(sl_hist.curSel>-1))
	{
		app._een(btn_form, (app.g.sl_getcolvalue(sl_hist,-1,"dataform")!=""));
				
		var intStage = app.g.sl_getcolvalue(sl_hist,-1,"fk_stage_id");
		var intCallref = app.g.sl_getcolvalue(sl_hist,-1,"fk_callref");	

		if(intStage!="" && intCallref!="")
		{
			//-- check if we have auth and/or task data to show
			var strParams = "cr="+app.pfu(intCallref)+"&sid="+intStage;
			var intAuthCount = app.g.sqs_rowcount("count/auths_for_call",strParams);
			//-- check auths
			if(intAuthCount>0)
			{
				app._slf(sl_wfauth, "_swc_fk_callref="+app.pfu(intCallref)+"&_swc_fk_stage_id="+intStage);
				TabHist.ShowTabItem(1,true);
			}
			else
			{
				TabHist.ShowTabItem(1,false);
			}
	
	
			var strParams = "cr="+app.pfu(intCallref)+"&sid="+intStage;
			var intTaskCount = app.g.sqs_rowcount("call/common/cnt_bp_tasks",strParams);
			//-- check tasks
			if(intTaskCount>0)
			{
				app._slf(sl_tasks, "_swc_bpm_parentcallref="+app.pfu(intCallref)+"&_swc_bpm_stage_id="+intStage);
				TabHist.ShowTabItem(2,true);
			}
			else
			{
				TabHist.ShowTabItem(2,false);
			}
			
			app._etab(TabHist, 0);
		}
			
	}
	else
	{
		app._een(btn_form, false);
		TabHist.ShowTabItem(1,false);
		TabHist.ShowTabItem(2,false);
	}

}

							btn_form_OnPressed()
{
	//-- show data form for this history record
	var strArgs="";
	var strDataForm = app.g.sl_getcolvalue(sl_hist,-1,"dataform");
	if(strDataForm!="")
	{
		app.OpenFormForEdit(app.g.sl_getcolvalue(sl_hist,-1,"dataform"), _swdoc.GetArg("callref"), strArgs, true, function(){});
	}

}

							sl_tasks1_OnItemDoubleClicked(nRow, nCol)
{
	// TODO: Add your event handler code here
	app.g.callingform = _swdoc.MF();
	app.global.OpenCallDetailsView(sl_tasks1.GetItemTextRaw(nRow, 'callref'));
	

}

							sl_wfauth_OnItemDoubleClicked(nRow, nCol)
{
	//-- open auth form with params
	var strKeyValue = sl_wfauth.GetItemTextRaw(nRow,"pk_auth_id");
	if(strKeyValue!="")	
		app.OpenFormForEdit("bpm_opencall_auth", strKeyValue, "history=1", true, function(){});

	return false;
}

							sl_audit_OnItemDoubleClicked(nRow, nCol)
{
	// TODO: Add your event handler code here
	return false;

}

							TabHist_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	if(nTab==0)
	{
		_swdoc.MF().sl_hist.Refresh();
	}
	else if (nTab==1)
	{
		_swdoc.MF().sl_wfauth.Refresh();
	}
	else if(nTab==2)
	{
		_swdoc.MF().sl_tasks.Refresh();
	}
	else if(nTab==3)
	{
		_swdoc.MF().sl_audit.Refresh();
	}

}
