						OnSaveData()
{
	if(!_swdoc.check_edit_rights())
		return false;
	
	_swdoc.setup_history();
	return true;
}

						function setup_history()
{
	 arrInserts = new Array();
	
	//-- get matching contract record
	var oRec = app.g.get_record("itsmsp_slr", _swdoc.oSLRD().pk_slr_id);
	var liveVal="";
	//-- for each field check if diff from contract
	for (x=0; x < dd.tables['itsmsp_slr'].columns.length;x++)
	{
	
		var colName = dd.tables['itsmsp_slr'].columns[x].Name;
					
		if(colName=="contract_notes")continue;
		
		//make sure the field exists
		if(oRec[colName]!=undefined)
		{
			
			//-- if values differ and value is not empty put into a field table
			
			var FORM_VALUE = itsmsp_slr[colName];
			
			//MessageBox(colName + " : " + FORM_VALUE + " : " + oRec[colName]);
			
			//-- if numeric value and = 0 then ignore
			if((app.g.dd_isnumeric('itsmsp_slr',colName))&&(FORM_VALUE==0))continue;
			
			var	dispFormVal = FORM_VALUE;
			var liveVal = oRec[colName];
			var dispLiveVal = liveVal;
			if(app.g.dd_isnumeric('itsmsp_slr',colName))
			{
				if(FORM_VALUE<0)
				{
					FORM_VALUE= app.g.fix_epoch(FORM_VALUE);
					dispFormVal = app.g.convert_epochddmmyyyy(FORM_VALUE);
					dispLiveVal = app.g.convert_epochddmmyyyy(liveVal);
				}
			}
			
			if((liveVal!=FORM_VALUE))
			{
				arrInserts.push(new Array( app.g.dd_fieldlabel('itsmsp_slr',colName),app.g.pfs(dispLiveVal) , app.g.pfs(dispFormVal)))
			}
		}	
	}

	_swdoc.arrDiary = arrInserts;
}

