//-- F0091281
function check_edit_rights()
{
	if(!app.session.HaveAppRight(app.ADMIN_SETTINGS, app.SLM_EDIT, app.session.dataDictionary))
	{
		app.global.MessageBox('Insufficient privileges to perform operation.\n\nYou have been denied the: [Can edit items in Service Level Management] permission.\n\nPlease contact your Supportworks system administrator', 16);
		return false;	
	}
	if(!app.session.HaveRight(ANALYST_RIGHT_C_GROUP,ANALYST_RIGHT_C_CANMODIFYSLA))
	{
		app.global.MessageBox('Insufficient privileges to perform operation.\n\nYou have been denied the: [Can modify SLAs] permission.\n\nPlease contact your Supportworks system administrator', 16);
		return false;	
	}
	return true;
}

function insertDiaryEntry(strDiaryText, strType)
{
	if (strType == undefined)
	{
		strType = "Detail Update";
	}
	var p = {};
	p.fsi = _swdoc.oSLRD().pk_slr_id;
	p.ut = strDiaryText;
	p.uc = strType;
	p.aid = app.session.analystId;							
	p.ux = 	app.g.todaysdate_epoch();										
 	app.g.submitsimplesql("itsmsp_slrd_diary.insert", p);
}

function insertSlaDiaryEntry(strDiaryText, strKey)
{
	var p = {};
	p.fsi = strKey;
	p.ut = strDiaryText;
	p.uc = "SLR Association";
	p.aid = app.session.analystId;							
	p.ux = 	app.g.todaysdate_epoch();										
 	app.g.submitsimplesql("itsmsp_slad_diary.insert", p);
}

function associate_cr(strCallRefs, intSlrId, strCode)
{
	var arrCallRefs = strCallRefs.split(",");  
	
	//-- loop through selected rfcs and link them
    for (var x=0;x<arrCallRefs.length;x++)
    {
        var rfc_callref = arrCallRefs[x];
        var rfc_callref_text = app.g.pad_callref(rfc_callref);

		//-- check that rfc is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.MF().sl_rfcs,rfc_callref,"callref");

		if (rowPos == -1)
		{ 		
			var strParams = "cr="+rfc_callref+"&slr="+intSlrId+"&rc="+app.pfu(strCode);
			app.g.submitsqs("slm/rfc_relate",strParams);

			strText = "Change Request" + " (" + rfc_callref_text + ") associated.";
			_swdoc.insertDiaryEntry(strText, "RFC Association");

			//-- update rfc diary
			strRfcText = "The '" + _swdoc.oSLRD().title + "' Service Level Requirement has been associated to this Change Request.";
			app.g.new_diaryevent(rfc_callref,strRfcText,"SLR Management","SLR Association");
		}
    } //-- eof for
	
	
}

function disassociate_cr(strCallRefs, intSlrId, relcode)
{
	var strParams = "cr="+strCallRefs+"&slr="+intSlrId+"&code="+relcode;
	app.g.submitsqs("slm/rfc_unrelate",strParams);
	
	var arrCallrefs = strCallRefs.split(',');
			
	for(var x=0;x<arrCallrefs.length;x++)
	{
		//-- update slr history
		strText = "Change Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated.";
		_swdoc.insertDiaryEntry(strText, "RFC Dis-association");
		
		//-- update change diary
		var strRFCText = "The '" + _swdoc.oSLRD().title + "' Service Level Requirement has been dis-associated from this Change Request.";
		app.g.new_diaryevent(arrCallrefs[x], strRFCText,"SLR Management","SLR Dis-association");
		
	}
}
function search_for_services()
{
    //JPG 22.01.2015
    //Defintion mode for the new window
	var strURL = "searchmode=1&multiselect=1&canreset=1";
	//Path of the new window. Search for services, better filtes
	app.g.popup("manage_portfolio", strURL, function(sResults)
	{
		if (sResults)
		{
			if (sResults.document.strSelectedKeys=="") return;  
	
			var strKeys = sResults.document.strSelectedKeys;
			var strCIText = sResults.document.strSelectedText;
			if(strKeys=="") return;
			//-- loop through selected CIs
			var strText = "";
			var strServiceText = "";
			var arrayCIkeys = strKeys.split(",");
			var arrayCItext = strCIText.split(",");	
			
			// -- SG, 02/07/2012, F0103324, required variable and array creation
			var itemType = "Service";
			var countBespokeTerms = 0;
			
			bespokeTermsArray = new Array();	
				
			for (var x=0;x<arrayCIkeys.length;x++)
    		{
        		var pk_ci_id = arrayCIkeys[x];
        		var ci_item_text = arrayCItext[x];
				
				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(_swdoc.MF().sl_services,pk_ci_id,0);
				if (rowPos == -1)
				{ 
					// -- SG, 02/07/2012, F0103324
					// Build array of CI's that have the Bespoke Terms field populated
					var rec = app.g.get_record("config_itemi", pk_ci_id);
					var strRel = "Uses";
                            		
					app.cmdb.generate_meconfigrelation(pk_ci_id,_swdoc.oSLRD().fk_cmdb_id,strRel);
					
					//-- Diary
					strText = "Service (" + rec.description + ") Associated.";
					_swdoc.insertDiaryEntry(strText, "Service Association");
					
				}
				
  			}   
  			var strSLAs = app.g.get_slrs_srs(itsmsp_slr.fk_cmdb_id);
			var strFilter = "swc=pk_auto_id&kvs="+strSLAs;
			app._slf(_swdoc.MF().sl_services , strFilter);
			// RJC F0104320
			_swdoc.MF().sl_services.Refresh();	
		}
		else
		{
			return;
		}
	});
}

						OnDataSaved()
{
	if(!_swdoc.boolInserting)
	{
		var strDiaryText = "";
		for(var intNumberOfInsert=0;intNumberOfInsert<_swdoc.arrDiary.length;intNumberOfInsert++)
		{
			arrDetails = _swdoc.arrDiary[intNumberOfInsert];
			if(strDiaryText!="")
			{
				strDiaryText += ",";
			}
			strDiaryText += arrDetails[0]+" has been modified from '"+arrDetails[1]+"' to '"+arrDetails[2]+"'";
		}
		if(strDiaryText!="")
		{
			//-- perform insert
			_swdoc.insertDiaryEntry(strDiaryText);
		}

	}
	else
	{
		var intCallRef = _swdoc.GetArg('fk_callref');
		if (intCallRef!="")
		{
			_swdoc.associate_cr(intCallRef, itsmsp_slr.pk_slr_id, "RFC-SLR");
		}
	}
	_swdoc.boolInserting=false;
	_swdoc.MF().Tab1.EnableTabItem(1,!_swdoc.boolInserting);
	_swdoc.MF().Tab1.EnableTabItem(2,!_swdoc.boolInserting);

  	var pDoc = app.g.get_me_document("ITSM_PROCESSES",1);
    if(pDoc)
    {
    	var intCurSel = pDoc.mainform.sl_slr.curSel;
    	pDoc.mainform.sl_slr.Refresh();
    	pDoc.mainform.sl_slr.SetRowSelected(intCurSel,true);
    }

}

