							btn_d_del_OnPressed()
{
	if(!_swdoc.check_edit_rights())
		return false;

	//-- ask to delete
	var strConfirm = "Are you sure you want to delete the selected discussion record?";
	
	var strSelectedId = sl_discussion.GetItemText(sl_discussion.curSel, 0);
	var strSelectedName = sl_discussion.GetItemText(sl_discussion.curSel, 1);
	
	if(app.g.sl_deldbrow(sl_discussion,sl_discussion.curSel,'pk_id',null,strConfirm))
	{	
		// ...insert audit entry for deleted discussion
		var strDiaryText = "'" + strSelectedName + "' discussion (ID=" + strSelectedId + ") deleted."
		_swdoc.insertDiaryEntry(strDiaryText);
		
		sl_discussion.Refresh();
		if (sl_discussion.rowCount()==0)
		{
			app._een(btn_d_view, false);
			app._een(btn_d_del, false);
		}
		else
		{
			sl_discussion.SetRowSelected(0, true, false);
		}
	}
}

