							TabRel1_OnTabSelected(nTab)
{
	//-- NWJ - 28.11.2006
	//-- Handle tab clicking
	tCMDB = 0;
	tServices = 1;
	tProblems = 2;
	tRFCs = 3;
	tSRs = 4;
	tKB = 5;
	tIncidents = 6;
	tCauses = 7;
	tMajIncidents = 8;	
	tChildIncidents = 9;
	tRAKb = 10;
	
	if(nTab==tCMDB)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_configitems.rowCount() < 1) sl_configitems.Refresh();
	}
	if(nTab==tProblems)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_problems.rowCount() < 1) sl_problems.Refresh();
	}
	else if(nTab==tRFCs)
	{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (sl_rfcs.rowCount() < 1) sl_rfcs.Refresh();
	}
	else if(nTab==tSRs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_srs.rowCount() < 1) sl_srs.Refresh();
	}
	else if(nTab==tKB)
	{
		if (sl_kbs1.rowCount() < 1)
		{
			app._slf(sl_kbs1,"cr="+OC().callref);	
			sl_kbs1.Refresh();
		}
	}	
	else if(nTab==tIncidents)
	{
		_swdoc.bool_slcustincs_displayed=true;
		Tab1_OnTabSelected(Tab1.tab);
	}
	else if(nTab==tMajIncidents)
	{
		if (sl_majinc.rowCount() < 1)sl_majinc.Refresh();
	}	
	else if(nTab==tChildIncidents)
	{
		if (sl_assinc.rowCount() < 1)sl_assinc.Refresh();
	}	
	else if(nTab==tServices)
	{
		if (sl_services.rowCount() < 1)
			sl_services.Refresh();
		// DTH: webclient
		_swdoc.boolAffectedServicesLoaded = true;
	}else if(nTab==tCauses)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_rels1.rowCount() < 1) sl_rels1.Refresh();
	}
	else if(nTab==tRAKb)
	{
		if (sl_rakb.rowCount() < 1)
		{
			app._slf(sl_rakb,"cr="+OC().callref);	
			sl_rakb.Refresh();
		}
	}		

}

