							OnFormLoaded(bDataReloaded)
{
	if(!app.session.HaveAppRight(app.ADMIN_SETTINGS, app.SLM_VIEW, app.session.dataDictionary)) 
	{
		MessageBox("You do not have permission to view SLM items.");
		_swdoc.CloseForm();
		return false;
	}
	
	//-- insert / update actions
	_swdoc.boolInserting = (_swdoc.oSLRD().pk_slr_id=="");
	
	if(_swdoc.boolInserting)
	{
		//-- set fields from args
		app.g.popuplate_form_fromargs("itsmsp_slr", _swdoc, true, true);
		_swdoc.UpdateFormFromData();
		app._ete(fld_business_owner, "");
		app._eva(fld_business_owner, "");
		itsmsp_slr.requested_by = "";
	}

	Tab1.ShowTabItem(1,!_swdoc.boolInserting);
	Tab1.ShowTabItem(2,!_swdoc.boolInserting);
		
	TabDetails.EnableTabItem(1,!_swdoc.boolInserting);
	TabDetails.EnableTabItem(2,!_swdoc.boolInserting);
}

