							btn_up_OnPressed()
{
	//-- F0091281
	if(!_swdoc.check_edit_rights())
		return false;
		
	//-- NWJ : move uri seq up 
	
	//-- get id to move up
	var currRow = sl_references.curSel;
	var intUpID = sl_references.GetItemTextRaw(sl_references.curSel, 'pk_caturi_id');
	var intUpSeq = sl_references.GetItemTextRaw(sl_references.curSel, 'nindex');	
	
	//-- get id to down
	var intDownID = sl_references.GetItemTextRaw(sl_references.curSel-1, 'pk_caturi_id');
	var intDownSeq = sl_references.GetItemTextRaw(sl_references.curSel-1, 'nindex');
	
	//-- update seq id for both 
	var p = {};
	p.ni = intDownSeq;
	p.pci = intUpID;
	if(app.g.submitsimplesql("itsmsp_slad.itsmsp_uri.setnindex", p))
	{
		var p = {};
		p.ni = intUpSeq;
		p.pci = intDownID;
		if(app.g.submitsimplesql("itsmsp_slad.itsmsp_uri.setnindex", p))
		{
			//-- refresh list
			sl_references.Refresh();
			sl_references.SetRowSelected(currRow-1, true, false);
		}
	}

}

