							btn_del_OnPressed()
{
	//-- F0091281
	if(!_swdoc.check_edit_rights())
		return false;
	
	var strTitle = sl_references.GetItemTextRaw(sl_references.curSel, "title");
	
	//-- ask to delete
	var strConfirm = "Are you sure you want to delete the selected reference record?";
	if(app.g.sl_deldbrow(sl_references,sl_references.curSel,'pk_caturi_id',null,strConfirm))
	{
		// ...insert audit entry for deleted reference
		var strDiaryText = "'" + strTitle + "' URI deleted."
		_swdoc.insertDiaryEntry(strDiaryText);
		
		
		//-- loop through list and set seq for each to #
			var intID=0;
			var nextSeq=0;
			for(var x=0; x < sl_references.rowCount();x++)
			{
				nextSeq=x+1;
				intID = sl_references.GetItemTextRaw(x, "pk_caturi_id");
				var p={};
				p.ni = nextSeq;
				p.pci = intID;
				app.g.submitsimplesql("itsmsp_slad.itsmsp_uri.setnindex", p);
			}
			
			sl_references.Refresh();
			sl_references.SetRowSelected(0, true, false);
			//sl_references(0);
	}
		
}

