	app._een(btn_down, ((nRow+1) < sl_references.rowCount()));
}

							mbtn_rfc_actions_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;
	var iAssociate = 2;
	
	//-- NWJ - unlink rfc
	if(nItemID == iDisassociate)
	{
		if(!_swdoc.check_edit_rights())
			return false;
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_rfcs,"callref",",");
		if(strCallRefs!= "")
		{
			var strPlural="";
			if (app.g.sl_getselected_count(_swdoc.MF().sl_rfcs)>1)
			{
				strPlural = "s";
			}
			if (!confirm("Are you sure you want to dis-associate the selected Change Request" + strPlural +"?")) return;
			
			_swdoc.disassociate_cr(strCallRefs, _swdoc.oSLRD().fk_cmdb_id, "RFC-SLR");
			
			//-- reload list
			_swdoc.MF().sl_rfcs.Refresh();
			if (_swdoc.MF().sl_rfcs.rowCount()==0)
			{
				app._een(btn_rfc_view, false);
			}
			else
			{
				_swdoc.MF().sl_rfcs.SetRowSelected(0, true, false);
			}
		}
	}
	else if (nItemID == iAssociate)
	{
   		if(!_swdoc.check_edit_rights())
			return false;
   		var strURL = "canreset=1";    	
    	app.itsm.search_calls("RFC",strURL,true,function(oResults)
    	{
    		if(oResults.selectedkeys=="") return;
			
			//-- link RFC to SLR
			_swdoc.associate_cr(oResults.selectedkeys, _swdoc.oSLRD().fk_cmdb_id, "RFC-SLR");
			
			//-- reload list
			_swdoc.MF().sl_rfcs.Refresh();
			_swdoc.MF().sl_rfcs.SetRowSelected(0, true, false);
    	});
	}
}

							sl_rfcs_OnItemSelected(nRow)
{
	//-- handle enable / disable
	var selCount = app.g.sl_getselected_count(_swdoc.MF().sl_rfcs);			
	app._een(_swdoc.MF().btn_rfc_view, (selCount==1));
}

							btn_rfc_view_OnPressed()
{
	if(_swdoc.MF().sl_rfcs.curSel > -1)
	{	
		_swdoc.MF().sl_rfcs.EditRecord(_swdoc.MF().sl_rfcs.curSel);
	}
		
}

							mbtn_sla_actions_OnMenuItem(strName, nItemID)
{
    var iAssociate = 1;
	var iDisassociate = 2;
	
	if(nItemID == iAssociate)
	{
		if(!_swdoc.check_edit_rights())
			return false;
		if(!app.oSettings.get_setting("boolFilterAppcodeSLALoaded"))
			app.g.load_appcode_sla();
		var strFilter = "";
		var strAppcodes = app.oSettings.get_setting("strFilterAppcodeSLA");
		if(strAppcodes!="")
			strFilter = "appcode in ('"+strAppcodes+"')";
		var strURL = "mode=select&filter="+app.pfu(strFilter);
		app.OpenForm("browse_slas", strURL, true, function(x)
		{
			var strKeys = x.document.strSelectedKey;
			if(strKeys=="")return;
			var arrKeys = strKeys.split(",");
			for (var x=0; x< arrKeys.length;x++)
			{
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_slad,fk_key,"pk_slad_id")==-1)
				{
           			var strRel = "Uses";
           			var recSLA = app.g.get_record("itsmsp_slad", fk_key);                               
					app.cmdb.generate_meconfigrelation(recSLA.fk_cmdb_id,_swdoc.oSLRD().fk_cmdb_id,strRel);  
				}			
			}
			var strSLAs = app.g.get_slr_slas(itsmsp_slr.fk_cmdb_id);
			var strFilter = "swc=fk_cmdb_id&kvs="+strSLAs;
			
			// RJC F0104320
			app.g.apply_appcode_sla_filter(mainform.sl_slad,'SLA-SLR',strFilter);
			//-- update slr history
			
			strText = "Service Level Agreement " + " (" + recSLA.slad_id + ") associated.";
			_swdoc.insertDiaryEntry(strText, "SLA Association");
			
			//- SLA Diary
			var strDiaryText = "Service Level Requirement (" + _swdoc.oSLRD().title + ") associated.";
			var p = {};
			p.fsi = fk_sla_id;
			p.ut = strDiaryText;
			p.uc = "SLR Association";
			p.aid = app.session.analystId;							
			p.ux = 	app.g.todaysdate_epoch();										
 			app.g.submitsimplesql("itsmsp_slad_diary.insert", p);
			
			_swdoc.MF().sl_slad.Refresh();
			sl_slad.SetRowSelected(0);
			sl_slad_OnItemSelected(0);
		});
	}
	else if(nItemID == iDisassociate)
	{
		if(!_swdoc.check_edit_rights())
			return false;
		var strID = app.g.sl_getselected_valuesdel(sl_slad,"pk_slad_id",",");
		if(strID!= "")
		{
			var strConfirm = "Are you sure you want to dis-associate the selected SLA??";
			if(confirm(strConfirm))
			{
				var fk_sla_id = app.g.sl_getprikeyvalue(sl_slad, sl_slad.curSel);
				var sla_id = app.g.sl_getcolvalue(sl_slad, -1, "slad_id");
				var strSLACMDBID = app.g.sl_getcolvalue(sl_slad, -1, "fk_cmdb_id");
				app.cmdb.delete_meconfigrelation(strSLACMDBID,_swdoc.oSLRD().fk_cmdb_id);
				
				var strSLAs = app.g.get_slr_slas(itsmsp_slr.fk_cmdb_id);
				var strFilter = "swc=fk_cmdb_id&kvs="+strSLAs;
				app.g.apply_appcode_sla_filter(mainform.sl_slad,'SLA-SLR',strFilter);
				// RJC F0104320
				_swdoc.MF().sl_slad.Refresh();
				
			}
			
			strText = "Service Level Agreement (" + sla_id + ") dis-associated.";
			_swdoc.insertDiaryEntry(strText, "SLA Dis-association");
			
			//- SLA Diary
			var strDiaryText = "Service Level Requirement (" + _swdoc.oSLRD().title + ") dis-associated.";
			var p = {};
			p.fsi = fk_sla_id;
			p.ut = strDiaryText;
			p.uc = "SLR Association";
			p.aid = app.session.analystId;							
			p.ux = 	app.g.todaysdate_epoch();										
 			app.g.submitsimplesql("itsmsp_slad_diary.insert", p);
 		}
	}


}

							btn_sla_view_OnPressed()
{
	var strSLA = _swdoc.MF().sl_slad.GetItemTextRaw(_swdoc.MF().sl_slad.curSel, "pk_slad_id");
	if (strSLA=="")return;
	
	_swdoc.MF().sl_slad.EditRecord(_swdoc.MF().sl_slad.curSel);
	_swdoc.MF().sl_slad.SetRowSelected(_swdoc.MF().sl_slad.curSel,true);
}

