							mbtn_cci_actions_OnMenuItem(strName, nItemID)
{

	var iRemove = 2;	
	if(nItemID == 1)
	{
		if(!_swdoc.check_edit_rights())
			return false;
		_swdoc.search_for_services();
	}else if(nItemID==iRemove)
	{
		if(!_swdoc.check_edit_rights())
			return false;
		var strID = app.g.sl_getselected_valuesdel(sl_services,"pk_auto_id",",");
		if(strID!= "")
		{
			var strConfirm = "Are you sure you want to dis-associate the selected Service??";
			if(confirm(strConfirm))
			{
			var fk_sla_id = app.g.sl_getprikeyvalue(sl_services, sl_services.curSel);
			var service_name = app.g.sl_getcolvalue(sl_services, -1, "description");
			var pk_auto_id = app.g.sl_getcolvalue(sl_services, -1, "pk_auto_id");
			app.cmdb.delete_meconfigrelation(pk_auto_id,_swdoc.oSLRD().fk_cmdb_id);
			
			var strSLAs = app.g.get_slrs_srs(itsmsp_slr.fk_cmdb_id);
			var strFilter = "swc=pk_auto_id&kvs="+strSLAs;
			app._slf(mainform.sl_services , strFilter);
			// RJC F0104320
			_swdoc.MF().sl_services.Refresh();
				
			
				strText = "Service (" + service_name + ") Dis-associated.";
				_swdoc.insertDiaryEntry(strText, "Service Dis-association");
			
			}
		}
	}


}

							mbtn_cci_views_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_services,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_services,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

							sl_services_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here
	var selCount = app.g.sl_getselected_count(_swdoc.MF().sl_services);			
	app._een(_swdoc.MF().mbtn_cci_actions, (selCount==1));
	app._een(_swdoc.MF().mbtn_cci_views, (selCount==1));
}
