var _swdoc = (IsObjectDefined("_bHtmlWebClient"))?top:_swdoc;
var strSelectedKeys= "";
var strSelectedText = "";
var strSelectedOther = "";
var currentList=null;
var boolSearchMode = false;

function MF()
{
	return _swdoc.mainform;
}

function setup_form_fields()
{
	//-- set field states
	app._een(_swdoc.MF().btn_select,false);
	app._een(_swdoc.MF().btn_subset,false);
	app._een(_swdoc.MF().btn_remsub,false);
	app._een(_swdoc.MF().btn_subclear,false);

	app._ete(_swdoc.MF().lb_abln,"Yes");

	app._een(_swdoc.MF().btn_delete,false);
	app._een(_swdoc.MF().btn_view,false);
	_swdoc.currentList = _swdoc.MF().sl_results;

	//-- show hide buttons depending on searchmode
	_swdoc.boolSearchMode = (_swdoc.GetArg("searchmode")=="1");

	app._evi(_swdoc.MF().btn_delete,(!_swdoc.boolSearchMode));
	app._evi(_swdoc.MF().btn_view,(!_swdoc.boolSearchMode));
	app._evi(_swdoc.MF().btn_select,(_swdoc.boolSearchMode));
	app._evi(_swdoc.MF().btn_cancel,(_swdoc.boolSearchMode));

	//_ete(_swdoc.MF().tb_flg_bundle,"1");
	app._eva(_swdoc.MF().tb_service_type,500);
	app._eva(_swdoc.MF().tb_flg_archived, 0);
	//-- F0098488
	//-- apply appcode to intial app.service filter if set
	if(dd.GetGlobalParamAsNumber("Application Settings/UseAppCodeFilter")==1)
	{
		strFilter = "sf=cis_meservicesonlyactive&_acin="+app.itsm.get_session_param("dataset");	
		app.g.sl_filter(_swdoc.currentList,strFilter,null,0);
	}

}

btn_search_OnPressed()
{
	//-- 19.01.2006 - NWJ
	//-- create search criteria

	app._een(btn_select,false);
	app.g.sqllist_clear(sl_results);


	var boolAND = (lb_type.value==1);
	var strFilter = app.g.create_search_params(mainform,"config_itemi","",boolAND,false);
	var strOrAnd = (boolAND)?" AND ":" OR ";

	//always make sure they are app.service catalog items being shown - so if set to 500 then remove value
	strFilter = strFilter.replace("_swc_service_type=500", "_swc_service_type=");	


	//always make sure they are app.service catalog items being shown
	if(strFilter!="")strFilter += "&";
	strFilter += "sf=cis_meservicesonly";
	if(_swdoc.MF().lb_subscription.value == 'Customer')
		strFilter += "&sub="+_swdoc.MF().lb_subscription.value+"&sub_val="+_swdoc.MF().tb_subscription_cust.value;
	if(_swdoc.MF().lb_subscription.value == 'Organisation')
		strFilter += "&sub="+_swdoc.MF().lb_subscription.value+"&sub_val="+_swdoc.MF().tb_subscription_org.value;
	if(_swdoc.MF().lb_subscription.value == 'Department')
		strFilter += "&sub="+_swdoc.MF().lb_subscription.value+"&sub_val="+_swdoc.MF().tb_subscription_dept.value;
	//-- apply filter
	app.g.sl_filter(_swdoc.MF().sl_results,strFilter,null,0);
	_swdoc.MF().sl_results.Refresh();

}

btn_clear_OnPressed()
{
	//-- clear search fields
	//MessageBox(tb_table.text + " : " + tb_table.value)
	app.g.clear_form_fields(tb_table.text,mainform,"",false);
	_swdoc.setup_form_fields();

}

btn_subclear_OnPressed()
{
	app.g.sl_filter(sl_sub,"empty=1",null,0);
	sl_sub_OnItemSelected(0);

}

btn_remsub_OnPressed()
{
	app.g.sl_clearselected(sl_sub,null,0);

	//-- reset filter

	var strSubCalls = app.g.sl_get_valuesdel(sl_sub,'pk_auto_id',",","");
	if(strSubCalls=="")strSubCalls="-1";
	var strFilter = "_kv=" + strSubCalls;	
	app.g.sl_filter(sl_sub,strFilter,null,0);
	sl_sub_OnItemSelected(0);	


}

btn_subset_OnPressed()
{
	//-- add selected items to the subset list
	var strSelKeys = app.g.sl_getselected_valuesdel(sl_results	,'pk_auto_id',",","");
	var strSubKeys = app.g.sl_get_valuesdel(sl_sub,'pk_auto_id',",","");

	//-- if we already have sub calls
	if(strSubKeys!="")
	{
		strSelKeys += "," + strSubKeys;
	}

	var strFilter = "_kv=" + strSelKeys;
	app.g.sl_filter(sl_sub,strFilter,null,0);

}

btn_cancel_OnPressed()
{
	_swdoc.CloseForm();


}

btn_select_OnPressed()
{
	//-- get list of selected callrefs
	_swdoc.strSelectedKeys= app.g.sl_getselected_valuesdel(_swdoc.currentList,tb_keycol.text,",");
	_swdoc.strSelectedText = app.g.sl_getselected_textdel(_swdoc.currentList,tb_txtcol.text,",");
	_swdoc.strSelectedOther = app.g.sl_getselected_textdel(_swdoc.currentList,tb_othercol.text,",");	
	_swdoc.CloseForm();	
}

OnFormLoaded()
{
	mainform.SetTimer(1,1);
	app._eva(_swdoc.MF().lb_subscription , '');
	app._ete(_swdoc.MF().lb_subscription , '');

}

sl_results_OnItemSelected(nRow)
{
	//--
	//-- enable the select button
	app._een(btn_view,(app.g.sl_getselected_count(_swdoc.currentList)==1));
	app._een(btn_delete,(sl_results.rowCount()>0));
	app._een(btn_select,(sl_results.rowCount()>0));
	app._een(btn_subset,(sl_results.rowCount()>0));


}

TabRes_OnTabSelected(nTab)
{
	if(nTab==0)
	{
		_swdoc.currentList = sl_results;
	}
	else
	{
		_swdoc.currentList = sl_sub;
		_swdoc.MF().sl_sub.Refresh();
	}

	app._een(btn_view,(app.g.sl_getselected_count(_swdoc.currentList)==1));
	app._een(btn_delete,(_swdoc.currentList.rowCount()>0));


}

btn_add_OnPressed()
{
	if(!app.service.can_create(true))
		return false;
	app.OpenFormForAdd("portfolio", "", "", true, function(){
		btn_search_OnPressed();
	});

}

btn_delete_OnPressed()
{
	if(!app.service.can_delete(true))
		return false;
	var strDeleteItems = app.g.sl_getselected_valuesdel(_swdoc.currentList,1,",");
	var strConfirm = "Are you sure that you want to delete the selected items from the database?"
	if(confirm(strConfirm))
	{
		boolDeleteBaselines = confirm("Would you like to delete all previous baselines for the selected items?");

		if(app.service.delete_serviceitem(strDeleteItems,boolDeleteBaselines))
		{
			//-- remove deleted items
			app.g.sl_clearrows(sl_results,strDeleteItems,1,null,0);
			app.g.sl_clearrows(sl_sub,strDeleteItems,1,null,0);
			sl_results_OnItemSelected(0);
			sl_sub_OnItemSelected(0);
		}
	}
}

btn_view_OnPressed()
{
	_swdoc.currentList.EditRecord(_swdoc.currentList.curSel);
}

btn_copy_OnPressed()
{
	//-- load records and copy

	//-- add selected items to the subset list
	var strSelKeys = app.g.sl_getselected_valuesdel(sl_results	,tb_keycol.text,",","");
	var arrKeys =strSelKeys.split(",");
	var intVar = app.global.GetCurrentEpocTime();
	for(var i=0;i < arrKeys.length;i++)
	{
		var catRec = app.g.get_record(tb_table.text, app.g.pfs(arrKeys[i]));
		var strNewKey = prompt("Please enter a new catlogue ID",app.g.pfs(arrKeys[i]) + "-SWCOPY");// + intVar + "-" + (i+1));

		app.g.copy_sw_record(tb_table.text ,catRec, "",strNewKey);
	}

	btn_search_OnPressed();
}

OnTimer(nTimer)
{
	_form.KillTimer(nTimer);
	_swdoc.setup_form_fields();


}

tb_catalog_type_OnLeftClick()
{
	app.OpenForm("service_type_picker", "", true, function(x)
	{
	if(x.document.strSaved)
		app._ete(tb_catalog_type,x.document.strConfigType);
	});
	return false;
}

sl_results_OnItemDoubleClicked(nRow, nCol)
{
	// TODO: Add your event handler code here
	var intRelKey = sl_results.GetItemTextRaw(nRow, 1);
	if(intRelKey=="")
		return false;
	app.OpenFormForEdit("portfolio", intRelKey, "", true, function(){
		btn_search_OnPressed();
	});


}

sl_sub_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here

	//-- enable the select button
	app._een(btn_view,(app.g.sl_getselected_count(_swdoc.currentList)==1));
	app._een(btn_delete,(sl_sub.rowCount()>0));
	app._een(btn_select,(sl_sub.rowCount()>0));
	app._een(btn_remsub,(sl_sub.rowCount()>0));
	app._een(btn_subclear,(sl_sub.rowCount()>0));


}

tb_catalog_type_OnRightClick()
{
	app._ete(tb_catalog_type,"");
	return true;
}

lb_subscription_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	if(strValue!='')
	{
		app._evi(_swdoc.MF().Text3, true);
	}else
	{
		app._evi(_swdoc.MF().Text3, false);
		app._evi(_swdoc.MF().tb_subscription_org, false);
		app._evi(_swdoc.MF().tb_subscription_cust, false);
		app._evi(_swdoc.MF().tb_subscription_dept, false);
	}
	if(strValue=='Organisation'){
		app._evi(_swdoc.MF().tb_subscription_org, true);
		app._evi(_swdoc.MF().tb_subscription_cust, false);
		app._evi(_swdoc.MF().tb_subscription_dept, false);
	}else if(strValue=='Department'){
		app._evi(_swdoc.MF().tb_subscription_org, false);
		app._evi(_swdoc.MF().tb_subscription_cust, false);
		app._evi(_swdoc.MF().tb_subscription_dept, true);
	}else if(strValue=='Customer'){
		app._evi(_swdoc.MF().tb_subscription_org, false);
		app._evi(_swdoc.MF().tb_subscription_cust, true);
		app._evi(_swdoc.MF().tb_subscription_dept, false);
	}

}
