action.attrib1:
if(_swdoc.OC().logcall_source=='SELFSERVICE')
{
	if(IsObjectDefined("_bHtmlWebClient"))
	{
		var getColourScheme = 4; //Default Grey
	}else
	{
		var getColourScheme = app.g.get_CurrentSkinColorSchema();
	}
	
    var strParams = 'in_callref='+_swdoc.OC().callref;
    var oRS = app.g.get_sqrecordset("call/common/count_wiz_qs",strParams);
                while(oRS.Fetch())
                {
                                var intCount = app.g.get_field(oRS,"counter");
                }
    if(intCount>0)
    {
                               var strServer = '&[app.webroot]';
		var strCurrentdd = '&[app.currentdd]';

                                app.global.OpenHtmlWindow("wssm_wiz_input", "frame", strServer + "/clisupp/details/"+ strCurrentdd +"/wssm_wiz_input.php?sessid="+app.session.sessionId+"&fk_callref="+_swdoc.OC().callref+"&ColourScheme="+getColourScheme, "Selfservice Wizard Input", true, 950, 700);
    }
    else
    {
        MessageBox("No Wizard input from selfservice.");
    }
}
else
{
                MessageBox('Call was not logged via selfservice.')
}

method.code:
						OnDataChanged()
{
	//-- 01.02.2005
	//-- NWJ
	//-- check problem code change
	//hideshow_majorincstab();
	
	//-- setup form fields - in case something important changed
	_swdoc.setup_crating()

	if (_swdoc.orig_probcode != _swdoc.OC().probcode)
   	{
   		//-- do we need to refresh inc probcode sqllist
   		//app.global.Sleep(150);
		//mainform.sl_incprofile.Refresh();

   		
   		_swdoc.orig_probcode = _swdoc.OC().probcode;
		
	    //--
    	//-- NWJ
    	//-- Check for prob/KE's and display message in textbox
		_swdoc.check_probprofile_against_problems_kes()    

		//-- check for problem count breach
		_swdoc.check_probprofile_against_incidentcount(_swdoc.OC().probcode,-1);
    }
    
    
    //-- status has changed
	if(_swdoc.strLastBPM_Status != _swdoc.OC().bpm_status_id)
	{
		_swdoc.strLastBPM_Status = _swdoc.OC().bpm_status_id;			
		//-- have to set time because form does not refresh properly
		if(IsObjectDefined('mainform'))
		{
			mainform.SetTimer(5005, 100);				
		}
	}
	
	if(_swdoc.intLastOCStatus != _swdoc.OC().status)
	{
		_swdoc.intLastOCStatus = _swdoc.OC().status;
		if(_swdoc.OC().status>15)
		{
			mainform.SetTimer(5005, 100);				
		}
		if(_swdoc.OC().status==6)
		{
			mainform.SetTimer(5005, 100);				
		}
	}
	    
	//TK This is Slowing Form Down
	//-- F0088933
	if(_swdoc.OC().last_text != _swdoc.MF().tb_ongoing.value)
	{
		//app.g.get_call_last_updatetxt(_swdoc.OC().callref,true,_swdoc.MF().tb_ongoing);
		_swdoc.load_diary_textfields(true);
	}    	
   

	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			app._een(_swdoc.MF().btn_actionsts, true);
	}

    
}

