//###20120608 - filtering out unwanted services
function filter_out_non_display_services(CIIDS){

	if(CIIDS=="")
		CIIDS = "-1";
	var strServiceKeys = "-1";
    var oRS = app.g.get_sqrecordset("call/incident/services_to_exclude","ci_id="+CIIDS);
    while (oRS.Fetch()){ 
		strServiceKeys +=",";
		strServiceKeys += app.g.get_field(oRS,"fk_cmdb_id");
	}
	return strServiceKeys;
}


function load_cis(boolFormLoaded)
{
//###20120718 reworked for multiple organisations/sites

// DTH: Want to replace the above code with the following
// Update the list of CI IDs based on fld_cis (By Customer etc)
// Append the relevant filter based on fld_ci_filter (Services Only etc)
// Apply the new filter to the list
// make sure that the relevant rows are checked

	if ((boolFormLoaded == undefined) || (boolFormLoaded == ""))
		boolFormLoaded = false;
	
	var strCIList = "";
	switch (_swdoc.MF().fld_cis.value)
	{
		case "by Organisation":
		case "by Settings: Organisation":
			_swdoc.current = "0";
    		strCIList = _swdoc.get_company_services_items(_swdoc.OC().fk_company_id);
			break;

		case "by Customer: Organisation":
			_swdoc.current = "0";
    		strCIList = _swdoc.get_company_services_items(_swdoc.oCust().fk_company_id);
			break;
			
		case "by Site":
		case "by Settings: Site":
			_swdoc.current = "0";
			strCIList = _swdoc.get_site_services_items(_swdoc.OC().site);
			break;
			
		case "by Customer: Site":
			_swdoc.current = "0";
    		strCIList = _swdoc.get_site_services_items(_swdoc.oCust().site);
			break;
			
		case "by Customer":
			_swdoc.current = "0";
    		strCIList = _swdoc.get_customer_services_items();
			break;
			
		case "by Contract":
			_swdoc.current = "0";
    		strCIList = _swdoc.get_contract_services_items();
			break;
			
		case "Currently Affected":
			_swdoc.current = "1";
			if (boolFormLoaded)
				strCIList = _swdoc.strOrigCallCis;
			else
 				strCIList = app.cmdb.get_call_cis(_swdoc.OC().callref, "INCIDENT", "");
			break;
	}
	var empty = 0;
	//-- If Empty Set it Empty
	if(typeof strCIList === "undefined"){
			app._slf(_swdoc.MF().sl_cust_cis,"empty=1");
			empty = 1;
			return;
	}
	if(strCIList == "0"){
			app._slf(_swdoc.MF().sl_cust_cis,"empty=1");
			empty = 1;
			return;
	}
	
	if (strCIList != "") // don't apply any filters if there are no CIs, to prevent a SQL error message
	{
		switch (_swdoc.MF().fld_ci_filter.value)
		{
			case "Services and Items":
				app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=" + strCIList + "&type=1&current=" + _swdoc.current);
				break;
			
			case "Services Only":
				app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=" + strCIList + "&type=2&current=" + _swdoc.current);
				break;
			
			case "Items Only":
				app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=" + strCIList + "&type=3&current=" + _swdoc.current);
				break;
		}
	}
	//--TK Refresh Needed -- If Not Empty
	if(empty==0)_swdoc.MF().sl_cust_cis.Refresh();
	// Need to check all of the rows when the form is loaded
	if (boolFormLoaded)
	{
		for(var y=0; y < _swdoc.MF().sl_cust_cis.rowCount();y++)
		{
			_swdoc.MF().sl_cust_cis.SetRowChecked(y, true); // all items in the list are linked to the call and should be checked
		}
	}
	else
	{
		_swdoc.uncheckset_customer_items();
	}
	
}

function setup_crating()
{   

	_swdoc.ib.StartAutoCycle(5000); //-- scroll every 10 seconds
	_swdoc.ib.RemoveItemsByGroup("rating");
	
	app._evi(_swdoc.MF().img_crating,true);
  	if(_swdoc.OC().c_rating==0)
  	{
  		app._eim(_swdoc.MF().img_crating,"&[app.webroot]" + "/clisupp/formimages/itsm/itsm_help.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request not yet rated");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Not yet rated", "", "", "rating");		
  	}
 	
  	if(_swdoc.OC().c_rating==2)
  	{
  		app._eim(_swdoc.MF().img_crating,"&[app.webroot]" + "/clisupp/formimages/itsm/cr_neu16.gif");
		app._ete(_swdoc.MF().lbl_crating,": Request satisfaction Neutral");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Neutral - " + _swdoc.OC().c_ratingtxt, "", "blue", "rating");				
  	}
	if(_swdoc.OC().c_rating==1)
	{
		app._eim(_swdoc.MF().img_crating,"&[app.webroot]" + "/clisupp/formimages/itsm/cr_pos16.gif");
		app._ete(_swdoc.MF().lbl_crating,": Request satisfaction Positive");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Positive - " + _swdoc.OC().c_ratingtxt, "", "green", "rating");						
	}
  	if(_swdoc.OC().c_rating==3)
  	{
  		app._eim(_swdoc.MF().img_crating,"&[app.webroot]" + "/clisupp/formimages/itsm/cr_neg16.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request satisfaction Negative");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Negative - " + _swdoc.OC().c_ratingtxt, "", "red", "rating");								
  	}

	var oUserdbRating = app.g.get_record("userdb_rating",_swdoc.oCust().keysearch,'fk_keysearch');
	if(oUserdbRating!=null)
	{
		app._ete(_swdoc.MF().lbl_open_request,": "+oUserdbRating.active_count);
		app._ete(_swdoc.MF().lbl_closed_request,": "+oUserdbRating.inactive_count);
	}

	//-- show overall rating
	switch(_swdoc.oCust().flg_ocrating)
	{
		case 1:
			app._eim(_swdoc.MF().img_orating, "&[app.webroot]" + "/clisupp/formimages/itsm/cr_neg16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction negative");
			break;			
		case 2:
			app._eim(_swdoc.MF().img_orating, "&[app.webroot]" + "/clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction neutral");
			break;						
		case 3:
		
			app._eim(_swdoc.MF().img_orating, "&[app.webroot]" + "/clisupp/formimages/itsm/cr_pos16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction positive");
			break;						
		default:
			app._eim(_swdoc.MF().img_orating, "&[app.webroot]" + "/clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction undetermined");
			break;
	}


}

function setup_form_fields()
{
	if (_swdoc.strOrigCallCis == "")
		_swdoc.strOrigCallCis = app.cmdb.get_call_cis(_swdoc.OC().callref, "INCIDENT", "");
		
	_swdoc.setup_crating();

// DTH: webclient - the parameter indicates that it is being called from the FormLoaded event. 
// In this case, _swdoc.strOrigCallCis can be used instead of making another call to the DB
	_swdoc.load_cis(true); 

	_swdoc.setup_inc_subtab();
	
	_swdoc.check_probprofile_against_problems_kes();
}

//-- check if we have any matching problems based on the select
//-- problem profile
function check_probprofile_against_problems_kes()
{         
	_swdoc.ib.RemoveItemsByGroup("profile");
    //-- now search for any problem calls that have the same profile code
    //--
    if(_swdoc.OC().probcode!='')
    {
		var strParams = "probcode="+app.pfu(_swdoc.OC().probcode);
		var intCount = app.g.sqs_rowcount("call/incident/prb_cnt_profcode",strParams);
	
    	if (intCount > 0 )        
    	{
        	//-- there are some matching problem calls for this problem profile
        	//-- so prompt the user using status bar (as requested by pre-sales)
        	_swdoc.ib.AddItem("There are Problems or Known Errors that potentially match this Incident.", "5", "red", "profile");
    		//_swdoc.UpdateFormFromData();
    		return;
    	}
    }
    
    _swdoc.check_problem_items();

}

function check_problem_items()
{
 	var strItems = _swdoc.get_item_list();
	if(strItems=="")
		return;

	var strParams = "ci_id="+app.pfu(strItems);
	var intCount = app.g.sqs_rowcount("call/incident/prb_cnt_related",strParams);

    if (intCount > 0 )        
    {
        //-- there are some matching problem call that have the same app.service or CI
        //-- so prompt the user using status bar (as requested by pre-sales)
        _swdoc.ib.AddItem("There are Problems or Known Errors that potentially match this Incident.", "5", "red", "profile");
    }
    _swdoc.UpdateFormFromData();
}

function setup_inc_subtab()
{
	var boolShowBreach = false;
	if(( app.g.fix_epoch(_swdoc.OC().fixbyx) < app.global.GetCurrentEpocTime() )&& (!(app.g.fix_epoch(_swdoc.OC().slafix)) || ((app.g.fix_epoch(_swdoc.OC().slafix))>0 && _swdoc.OC().withinfix==0)))
	{
		_swdoc.MF().TabDesc.ShowTabItem(4,true);
		//_etab(_swdoc.MF().TabDesc,0);
		boolShowBreach = true;
		
		// get list of support groups logged in analyst is a member of
		var strAnalystGroups = app.g.get_all_analyst_groups(app.session.analystId);
		var arrAnalystGroups = strAnalystGroups.split(",");
		var index;
		var boolInGroup=false;
		// loop through list of support groups
		for (index in arrAnalystGroups){
			// check if the support group this call is assigned to is in list of groups
			if(arrAnalystGroups[index]==_swdoc.OC().suppgroup){
				boolInGroup=true;
				break;
			}
		}
		/* if logged in analyst is the owner of this call or is the member of
		   the support group this call is assigned to make the Breach Text field mandatory */
		if(_swdoc.OC().owner==app.session.analystId || boolInGroup) {
			app._ema(_swdoc.MF().tb_breachtext,true);
		}
	}

	var boolShowRes = (_swdoc.OC().resolve_text!="")
	_swdoc.MF().TabDesc.ShowTabItem(3,boolShowRes);
	
	var boolShowClose = (_swdoc.OC().close_text!="" && _swdoc.OC().status>14);
	_swdoc.MF().TabDesc.ShowTabItem(5,boolShowClose);
	
	if (!boolShowBreach && !boolShowRes && !boolShowClose)
	{
		// DTH: webclient - load last diary entry and remove the existing call from _swdoc.OnFormLoaded
		//_swdoc.load_diary_orig();
		_swdoc.load_diary_textfields(true);
		app._etab(_swdoc.MF().TabDesc,0);
	}
	else
	{
		if (boolShowBreach) app._etab(_swdoc.MF().TabDesc,4);
		if (boolShowRes) app._etab(_swdoc.MF().TabDesc,3);
		if (boolShowClose) app._etab(_swdoc.MF().TabDesc,5);
	}
}

//-- called when loading form or saving/changing data
var boolMI = false;
function hideshow_majorincstab()
{
	var currMI = (_swdoc.OC().itsm_majorinc=="1");
	_swdoc.EF().TabRel.ShowTabItem(9,currMI);
	app._etab(_swdoc.EF().TabRel, 0);
	_swdoc.boolMI = currMI;
}

function load_customer_items()
{
	if(_swdoc.oCust().keysearch=="")
	{
		app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=-1");
		//MF().lbl_manager.text =  ": ";
		return;	
	}

	//-- load users config items	
	var userCIS = app.cmdb.get_me_cis("'" + app.g.pfs(_swdoc.oCust().keysearch) + "'" , "CUSTOMER","cilist_active_cisonly");
	app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=" + userCIS);
	
	//-- check those that are linked to this incident - have to use timer
	_swdoc.uncheckset_customer_items();
	
}

