// DTH: webclient
function get_customer_services_items()
{
	if(_swdoc.oCust().keysearch=="")
	{
		return;	
	}
	//var userCIS = app.cmdb.get_me_cis("'" + app.g.pfs(_swdoc.oCust().keysearch) + "'" , "CUSTOMER",""); // additional filters for Services and / or items will be added later
	var userCIS = app.cmdb.get_me_cis(_swdoc.oCust().keysearch, "CUSTOMER","");
	var serviceKeys = app.service.get_customer_services(_swdoc.oCust().keysearch,true);

	//###20120608 - filtering out unwanted services
	if(app.boolServiceCIBehaviourOnIncident) serviceKeys = _swdoc.filter_out_non_display_services(serviceKeys);

	if(serviceKeys=="")
		serviceKeys = "-1";
	if(userCIS=="")
		userCIS = "-1";
	userCIS = serviceKeys + "," + userCIS;
	return userCIS; 
}

// DTH: webclient
function get_company_services_items(strCompanyID)
{


	//###20120718 rewrite to cope with multiple organisations
	if(strCompanyID=="")
	{
		return
	} 
	if(typeof _swdoc.multi_companyCIIDS[strCompanyID] === "undefined")
		_swdoc.get_company_cis(strCompanyID);
		
	// DTH: webclient - add the following from _swdoc.load_company_services
	var userCIS = _swdoc.multi_companyCIIDS[strCompanyID];
	var serviceKeys = app.service.get_organisation_services(strCompanyID,true);
		
	//###20120607 - filtering out unwanted services
	if(app.boolServiceCIBehaviourOnIncident) serviceKeys = _swdoc.filter_out_non_display_services(serviceKeys);

	if(serviceKeys=="")
		serviceKeys = "-1";
	userCIS = serviceKeys + "," + userCIS;

	return userCIS; 
}

// DTH: webclient
function get_contract_services_items()
{
	if(_swdoc.OC().fk_contract_id=="")
	{
		return;
	}
	if(typeof _swdoc.multi_contractCIIDS[_swdoc.OC().fk_contract_id] === "undefined")
		_swdoc.get_contract_cis();
	
	var userCIS = _swdoc.multi_contractCIIDS[_swdoc.OC().fk_contract_id];
	var serviceKeys = app.cfg.get_contract_cikeys(_swdoc.OC().fk_contract_id,true);
	
	//###20120607 - filtering out unwanted services
	if(app.boolServiceCIBehaviourOnIncident) serviceKeys = _swdoc.filter_out_non_display_services(serviceKeys);

	if(serviceKeys=="")
		serviceKeys = "-1";
	userCIS = serviceKeys + "," + userCIS;
	return userCIS; 
}

function get_contract_cis()
{
	//###20120717 - rewrite to allow for multiple organisations
	var strKeyValues = app.g.get_contract_cikeys(_swdoc.OC().fk_contract_id,true,true);	

	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_contractCIIDS[_swdoc.OC().fk_contract_id] = strKeyValues;
}

// DTH: webclient
function get_site_services_items(strSiteID)
{

	//###20120717 rewrite to cope with multiple sites
	if(strSiteID=="")
	{
		return
	} 

	if(typeof _swdoc.multi_siteCIIDS[strSiteID] === "undefined")
		_swdoc.get_site_cis(strSiteID);
		
	// DTH: webclient - add the following from _swdoc.load_site_services
	var userCIS = _swdoc.multi_siteCIIDS[strSiteID];

	var serviceKeys = app.service.get_site_services(strSiteID,true);
	//###20120607 - filtering out unwanted services
	if(app.boolServiceCIBehaviourOnIncident) serviceKeys = _swdoc.filter_out_non_display_services(serviceKeys);

	if(serviceKeys=="")
		serviceKeys = "-1";
	userCIS = serviceKeys + "," + userCIS;
	
	return userCIS; 
}

//-- uncheck then re check those that exist
function uncheckset_customer_items()
{
	for(var y=0; y < _swdoc.MF().sl_cust_cis.rowCount();y++)
	{
		_swdoc.MF().sl_cust_cis.SetRowChecked(y, false);
	}


	//-- check those that are linked to this incident
	if(IsObjectDefined("extform.sl_configitems"))
	{
		var strCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems,'pk_auto_id',",");
		var arrCIS = strCIs.split(",");
		for(var y=0; y < arrCIS.length;y++)
		{
			var intRowPos = app.g.sl_findrow_byvalue(_swdoc.MF().sl_cust_cis, arrCIS[y], 'pk_auto_id');	
			if(intRowPos>-1)
			{
				//MessageBox("CORE PRODUCT ISSUE : check asset row " + intRowPos);
				_swdoc.MF().sl_cust_cis.SetRowChecked(intRowPos, true);
			}
		}	
	}

// DTH: webclient - Replace the above with the following
	if(IsObjectDefined("extform.sl_services"))
	{
		if (!_swdoc.boolAffectedServicesLoaded) // will be false if analyst has not selected the Affected Services tab on Assoc Info tab
		{
			if (_swdoc.EF().sl_services.rowCount() < 1)
				_swdoc.EF().sl_services.Refresh();
			_swdoc.boolAffectedServicesLoaded = true;
		}
		var strCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,'pk_auto_id',",");
		var arrCIS = strCIs.split(",");
		for(var y=0; y < arrCIS.length;y++)
		{
			var intServRowPos = app.g.sl_findrow_byvalue(_swdoc.MF().sl_cust_cis, arrCIS[y], 'pk_auto_id');
			if(intServRowPos>-1)
			{
				//MessageBox("CORE PRODUCT ISSUE : check service row " + intServRowPos);
				_swdoc.MF().sl_cust_cis.SetRowChecked(intServRowPos, true);
			}
		}	
	}

}





//-- 02.03.2006
//-- check if breached incident count
function check_probprofile_against_incidentcount(strProbCode, intAlertTrigger)
{
	//-- if alert trigger is not specified we need to get counter from probcode table
	_swdoc.intProblemAlertPeriod = 0;
	_swdoc.intFirstProblemIncidentDatex = 0;
	_swdoc.intPeriodBreach = 0;	
	_swdoc.incident_profile_count	= 0;
	
	if (intAlertTrigger==-1)
	{
		var oRec = app.g.get_record("pcinfo",strProbCode);
        if (oRec)
        {
        	intAlertTrigger = new Number(oRec.user2);
        	_swdoc.intProblemAlertPeriod  = oRec.user3;
			_swdoc.intProblemAlertPeriod--;_swdoc.intProblemAlertPeriod++;
		  	_swdoc.intFirstProblemIncidentDatex = oRec.first_incident_datex;    	
			_swdoc.incident_profile_count = oRec.incident_count;	
        }
        else
        {
        	intAlertTrigger = 0;
        }
        
        //-- switched off
        if(_swdoc.intProblemAlertPeriod =="")_swdoc.intProblemAlertPeriod = 0;
	}

	_swdoc.incident_profile_count++;
	_swdoc.boolProblemAlert = false;
    if (intAlertTrigger > 0)
    {		
		//-- if greater than alert analyst of possible problem
		if (_swdoc.incident_profile_count >= intAlertTrigger)
		{
			//-- we need ot raise some sort of alert when the call is actually logged
			_swdoc.boolProblemAlert = true;

			//-- if this profile uses an alert period (ie number of incidents rasied within an hour or 2 etc		
			if(_swdoc.intProblemAlertPeriod > 0)			
			{
				testDatex = app.global.GetCurrentEpocTime() - _swdoc.intFirstProblemIncidentDatex;
				testPeriod = _swdoc.intProblemAlertPeriod * 3600; //-- get period in seconds
				
				
				//-- if number of incidents has exceed limit and within period - then signal alert to pm
				if (testDatex < testPeriod)
				{
					_swdoc.intPeriodBreach = 1;
					
					
					//-- 07.11.2006 - NWJ
					//-- check if we should tell the analyst (some customer will want this others wont) 
					//-- so if you are configuring just switch to 0 in dd
					if(dd.GetGlobalParamAsString("Application Settings/Incident/AlertIncidentProfileBreach")==1)
   					{
						MessageBox("There may be a potential problem. There are currently " + intCount + " Incidents that have this incident profile.");
					}	
				}
			}
			else
			{
				//-- profile doesnt use period limit just incident limit
				_swdoc.intPeriodBreach = 1;
			}
		}		
    }    
    _swdoc.process_problem_breach();
}


function process_problem_breach()
{
    	//-- check if this is thw first incident to be logged against this profile
    	var intCurrentEpoch = app.global.GetCurrentEpocTime();
    	intCurrentEpoch++;intCurrentEpoch--;
		if(_swdoc.intFirstProblemIncidentDatex==0)
		{
			//-- update the pcinfo table
			var strParams = "ep="+intCurrentEpoch+"&pc="+app.pfu(_swdoc.OC().probcode);
			app.g.submitsqs("call/incident/pm_set_firsttime", strParams, false);
		}
    
	   	//--
    	//-- use serverside to the alert pms
    	//--
		if (_swdoc.boolProblemAlert)    	
		{
    		var xmlmc = new XmlMethodCall;
			xmlmc.SetValue("inc_callref", _swdoc.OC().callref);
			xmlmc.SetValue("inc_callref_long", app.g.pad_callref(_swdoc.OC().callref));		
			xmlmc.SetValue("probcode", _swdoc.OC().probcode);
			xmlmc.SetValue("probcode_text", _swdoc.MF().tb_probprofile.text);		
			_swdoc.incident_profile_count++;_swdoc.incident_profile_count--;
			xmlmc.SetValue("counter", _swdoc.incident_profile_count);				
			xmlmc.SetValue("currentdatetime", intCurrentEpoch);							
			xmlmc.SetValue("alertpm", _swdoc.intPeriodBreach);									   

			if(xmlmc.Invoke("vpme", "itsmProblemProfileMonitor"))
			{
	    		//-- get the result
				var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
				var ObjRes = app.XMCResult(strXML);
				if(!ObjRes)
				{
	        		MessageBox("Failed to Raise Problem Alert correctly (" + objRes.message + "). Please contact your Supportworks Supervisor.");
	    		}	
    		}
    		else
    		{
    			//MessageBox(xmlmc.GetReturnXml())
    			MessageBox("Failed to run VPME script ('ITSM - Problem Alert'). " + xmlmc.GetLastError() + ". Please contact your Supportworks Supervisor.");
    		}
		}
		else
		{
			//-- no problem alert required
			//-- increase incident count
			var strParams = "cnt="+_swdoc.incident_profile_count+"&pc="+app.pfu(_swdoc.OC().probcode);
			app.g.submitsqs("call/incident/pm_set_count", strParams, false);
		}   
}

function link_incident_problems(strProblemIDS, strClasses)
{
	var array_inc = strProblemIDS.split(",");  
	var array_classes = strClasses.split(",");
 	var strText = "This incident has been associated with the following Problems and Known Errors\n\n";
	var boolPRBs= false;
	
	//-- loop through selected problems and link them
    for (var x=0;x<array_inc.length;x++)
    {
        var prb_callref = array_inc[x];
        var prb_callref_text = app.g.pad_callref(prb_callref);

		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_problems,prb_callref,0);
		if (rowPos == -1)
		{ 
			boolPRBs = true;
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(prb_callref,_swdoc.OC().callref,'PROBLEM-INCIDENT'))
			{
				strText += array_classes[x] + " (" + prb_callref_text + ") associated\n";
				
				//-- update ke or problem diary
				strPrbText = "The incident (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this " + array_classes[x];
				app.g.new_diaryevent(prb_callref,strPrbText,"Incident Management","Incident Association");
				if(array_classes[x]=="Known Error")
				{
					var xmlmc = new XmlMethodCall;
	
					xmlmc.SetParam("keCallref", prb_callref);
					xmlmc.SetParam("incCallref", _swdoc.OC().callref);
 									
					// Invoke the VPME method LogCall. 				
					if(xmlmc.Invoke("VPME", "keAssociation"))
					{
 					}
					else
					{
    					// Error
    					MessageBox(xmlmc.GetLastError());
					}
				}
			}
		}
    } //-- eof for

	//-- create incident call diary update then reload list
	if(boolPRBs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Problem Association");
		_swdoc.EF().sl_problems.Refresh();
	}	
}

function load_diary_textfields(boolLastOnly)
{
	//-- load full diary text into form fields - bug fix 62811
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_last_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_ongoing);
	if((boolLastOnly==undefined)||(!boolLastOnly))app.g.get_call_first_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_origtext);
}

function load_diary_orig()
{
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_first_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_origtext);
}

