function get_company_cis(strCompID)
{
	//###20120717 - rewrite to accommodate multiple organisations
	var strKeyValues = app.cfg.get_company_cikeys(strCompID,true,true);	

	var strParams = "fk_company_id="+app.pfu(strCompID);
    var oRS = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oRS.Fetch())
	{
		if(strKeyValues!="")strKeyValues+=",";	
		var strSiteName = app.g.get_field(oRS,"site_name");
		strKeyValues+= app.cmdb.get_site_cis(strSiteName);
	}	
	
	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_companyCIIDS[strCompID] = strKeyValues;


}

function check_cis()
{
	if(_swdoc.EF().sl_configitems.rowCount() =="")
		return;
	var boolCIRemoved = false;
	if(_swdoc.companyCIIDS == "")
		_swdoc.get_company_cis();
	var arrCIS = _swdoc.companyCIIDS.split(",");
	for (var x=_swdoc.EF().sl_configitems.rowCount();x > -1;x--)
	{
		var strValue = _swdoc.EF().sl_configitems.GetItemText(x,1);
		if(strValue !=""){
			if(app.in_array(arrCIS,strValue))
			{
				_swdoc.EF().sl_configitems.SetRowSelected(x,false,true);
			}
			else
			{
				_swdoc.EF().sl_configitems.SetRowSelected(x,true,true);
				boolCIRemoved = true;
			}
		}
	}
	_swdoc.EF().mbtn_ici_actions_OnMenuItem('test',1);
	if(boolCIRemoved)
		MessageBox("CIs were removed from this call, as they were not associated with the currently selected Organisation");

}

function get_item_list()
{
	var strCIs = "";
	if(IsObjectDefined("extform"))
	{
     	var strCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems,0,",");
    	var strServices = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,0,",");
    	if(strServices!="")
    	{
    		if(strCIs=="")
    		{
    			strCIs = strServices;
    		}else
    			strCIs +=","+strServices;
    	}
    }
    else
    {
 		var strCIs = app.cmdb.get_call_cis(_swdoc.OC().callref , "INCIDENT","");
    }
    return strCIs;
}


