//--
//-- based on the _swdoc.current workflow stage show / hide tabs / buttons etc
function setup_process_fields(boolDocChange)
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		if(_swdoc.strCurrentWorkflow != _swdoc.OC().bpm_workflow_id)
		{
			_swdoc.arrAudit = new Array();
			var strParams = "fk_workflow_id="+app.pfu(bpm_workflow.pk_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
			}
			_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		}
	}//-- F0105032
	
	//-- NWJ
	//-- assign _swdoc to app.cfg array (so we can refresh tasks list from task forms
	app.cfg.allocaterfcdoc(_swdoc.OC().callref,_swdoc)
		
	_swdoc.strOriginalSLA = _swdoc.OC().priority;
	
	// no process so do not show process tabs
	if(_swdoc.OC().bpm_workflow_id=="")
	{
		_swdoc.MF().TabAssess.ShowTabItem(4, false);
		app._etab(_swdoc.MF().TabAssess, 0);
		return true;
	}

	//--
	//-- check if we need to initialise stage (in case something has gone wrong somwhere in vpme)
	if((_swdoc.OC().bpm_workflow_id!="")&&(_swdoc.OC().bpm_stage_id < 1))
	{
		var oRec = app.g.get_record("bpm_workflow", _swdoc.OC().bpm_workflow_id);
		if(oRec)
		{
			var strStageID = oRec.fk_firststage_id;
			_swdoc.ChangeRecord("bpm_stage", strStageID)			
		}	
	}
	
	//-- if user can add tasks and this stage allows it
	var boolActive = false;
	if(_swdoc.OC().status<16)
		boolActive = true;

	//-- if user can add tasks and this stage allows it
	var strParams = "cr="+app.pfu(_swdoc.OC().callref)+"&sid="+_swdoc.oStage().pk_stage_id;
	var intTaskCount = app.g.sqs_rowcount("call/common/cnt_bp_tasks",strParams);

    var boolHasTasks = intTaskCount>0;
    var boolAddTasks = ( (_swdoc.oStage().flg_addcalltasks==1) &&(boolActive)&&(app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDTASK,app.session.dataDictionary)) )
	var boolShowTasks = ((_swdoc.oStage().flg_hastasks==1)||(boolAddTasks)||(boolHasTasks));
	//-- F0090013 only show tasks tab if there are tasks or can add on fly - as stage may have tasks, but they may not be created
	var boolShowTasks = ((boolAddTasks)||(boolHasTasks));

	app._een(_swdoc.MF().mbtn_mod_task1, boolAddTasks);
	_swdoc.MF().TabAssess.ShowTabItem(6,boolShowTasks);
	if((boolShowTasks)&&(boolDocChange))
	{
		//-- NWJ - let SW handle refreshing ...
		//MF().sl_tasks.Refresh();
	}
	
	//-- AUTH STUFF
	var boolRequiresAuth = (_swdoc.OC().bpm_waitingauth==1);	
	var boolAddAuths = ( (_swdoc.oStage().flg_addcallauths==1) && (app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDAUTH,app.session.dataDictionary)) )
	if(_swdoc.oStage().flg_unanimous_auth==1)
	{
		app._evi(_swdoc.MF().authWeighting1, false);
	}	
	//-- if we have auth show tab and disable status lb
	var boolShowAuths = boolRequiresAuth||boolAddAuths;
	_swdoc.MF().TabAssess.ShowTabItem(5, boolShowAuths);
	
	//if we require authorisation, or disable manual, do not enable status
	//--only disable manual progression if stage is marked to disable AND stage has tasks (or requires auth)
	var boolTaskStop = _swdoc.oStage().flg_disable_manual==1 && boolHasTasks;
	var boolEnableStatus = (!(boolTaskStop|| boolRequiresAuth));
	app._een(_swdoc.MF().lb_wf_state1, boolEnableStatus);
	
	app._evi(_swdoc.MF().mbtn_mod_auth1,boolAddAuths &&(boolActive));	
	//-- EOF AUTH STUFF
	
	//-- if we have data form show button
	app._een(_swdoc.MF().btn_bpm_form1, ((_swdoc.oWF().ext_sw_form!="")||(_swdoc.oStage().swdataform!="")));

	//-- select tab 0 or 1 depending on auth
	app._etab(_swdoc.MF().TabAssess,(boolRequiresAuth)?5:0);
	
	if(boolDocChange)
			app.g.reset_bpm_form_attribute_check(_swdoc);		
	app.g.process_bpm_form_attribute_check(_swdoc, _swdoc.OC().bpm_workflow_id, _swdoc.oStage().pk_stage_id);

	return true;
}

// F0110887 START
// Creating a function to Change the Customer record. This can be called from the Show Me menu and from the Action menu.
// This approach means that we can control access to this action
function search_for_customer(funcCallback)
{
	if(!app.itsm.can_edit(_swdoc.OC().callclass))
	{
		if ((funcCallback != undefined) && (funcCallback != null))
		{
			funcCallback();
		}
	}
	else
	{	
		var strURL = "autorun=1&searchtype=1&canreset=1&fk_company_id=" + _swdoc.oCompany().pk_company_id;
		app.g.search_for("customers",false,strURL,function(sResults)
		{
			if(sResults.selectedkeys=="")
			{
				if ((funcCallback != undefined) && (funcCallback != null))
				{
					funcCallback();
				}
			}
			else
			{
    			var arrKeySearch = sResults.selectedkeys.split(",");
				var arrCompanyId = sResults.selectedcompanyids.split(",");
  				
    			if(arrKeySearch[0]!=_swdoc.oCust().keysearch)
    			{
      				//-- Get Company Info
      				var strParams = "table=userdb&_swc_keysearch=" + app.pfu(arrKeySearch[0]);
					var oRS = app.g.get_sqrecordset("select.tablebycol",strParams);
					if(oRS.Fetch())
					{
						var strOrgID = oRS.GetValueAsString('fk_company_id');
					}
      				var oRS = app.g.get_sqrecordset("company/get_expiry_date","company_id="+strOrgID);
  					if (oRS.Fetch())
  					{ 
      					var intExpiryEpoch = app.g.fix_epoch( app.g.get_field(oRS,"SUPPORTEXPIRYX"));
					}
					var boolOrgSupported = new Boolean(app.g.checkOrgSupport(strOrgID, intExpiryEpoch, true));
					if (boolOrgSupported==true)
					{
    					_swdoc.ChangeRecord("userdb", arrKeySearch[0]);
					}
				}
				if ((funcCallback != undefined) && (funcCallback != null))
				{
					funcCallback();
				}
			}
		});
	}
}
// F0110887 END

						OnRecordChanged(strTable)
{
	
	if(strTable=="userdb")
	{
		//-- check customer status message
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
		
		var boolExternalCompany = (dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1);

		//-- nwj - 2.3.2 - load company record for customer - and show company on resolve message
		if(!boolExternalCompany && _swdoc.oCust().fk_company_id != "" && app.LC(_swdoc.oCust().fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
		{
			//-- rjc - 2.4 - resolve company record if company doesnt exist
			_swdoc.ChangeRecord('Company',_swdoc.oCust().fk_company_id);
			if(_swdoc.oCompany())
			{
				//-- apply diary
 				var strText = 'The related record [Company] was changed for this call. Old Value: "'+_swdoc.strOrigCompany +'", New Value: "'+_swdoc.oCompany().companyname+'"';
 				_swdoc.strOrigCompany =_swdoc.oCompany().companyname;
				app.g.new_dairyevent(_swdoc.OC().callref,strText,"","");	

				_swdoc.boolResolvingCust = false;

				if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
			}
			else
			{
				//-- there is adata issue here as the customers organisation does not exist
				MessageBox("The organisation that this customer belongs to cannot be found on the system. Please contact your Supportworks administrator.");
			}
		}
		
		_swdoc.load_cis();
	}
	if(strTable=="company")
	{
		//-- apply diary
 		var strText = 'The related record [Company] was changed for this call. Old Value: "'+_swdoc.strOrigCompany +'", New Value: "'+_swdoc.oCompany().companyname+'"';
 		_swdoc.strOrigCompany =_swdoc.oCompany().companyname;
		app.g.new_dairyevent(_swdoc.OC().callref,strText,"","");	

 		var funcFinalProcessing = function()
		{
 			//###20120720 - ensure that site etc gets empties on new company selection.
			if( _swdoc.boolEmptySites ){
			
				_swdoc.OC().costcenter = "";
				_swdoc.OC().fk_dept_code = "";
				_swdoc.OC().fk_contract_id = "";
				_swdoc.MF().lb_contract.value = "";
				_swdoc.MF().lb_contract.text = "";
				_swdoc.MF().fld_oc_dept.value = "";
				_swdoc.MF().fld_oc_dept.text = "";
				_swdoc.Save();
				_swdoc.OC().site = "";
			}
			
			app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract);	
		
			_swdoc.load_cis();
			
			//-- nwj - 2.3.2 - load company record for customer - and show company on resolve message
			if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
		
 			_swdoc.check_cis();
		
			_swdoc.UpdateFormFromData();
		}
		
 		if(dd.GetGlobalParamAsNumber("Application Settings/Incident/OnOrganisationResolveCustomer")==1)
		{	
 			var strURL = "autorun=1&searchtype=1&canreset=1&fk_company_id=" + _swdoc.oCompany().pk_company_id;
			app.g.search_for("customers",false,strURL,function(sResults)
			{
				if(sResults.selectedkeys!="")
    			{ 
    				var arrKey = sResults.selectedkeys.split(",");
 					_swdoc.ChangeRecord("userdb",  arrKey[0]);
 				}
 				else
 				{
 					//###20120718 When supporting external companies, then IF No customer selected (i.e. cancelled), then Old Customer should stay and be changed by using Customer Search
					var boolExternalCompany = (dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1);
 			 		if (!boolExternalCompany) _swdoc.ResetRecord('userdb');
				}
				funcFinalProcessing();
			});
    		
		}
		else
		{	
 			 if (dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")!=1) _swdoc.ResetRecord('userdb');
 			 //###20120727 reset record only when NOT dealing with external companies
 			 funcFinalProcessing();
 		}
	}
}

