						OnFormClosing()
{
	if(IsObjectDefined("mainform"))
	{	
		//-- NWJ
		//-- remove from openrfcs array
		app.cfg.deallocaterfcdoc(_swdoc.OC().callref)
		app.g.remove_me_document("INCIDENT", _swdoc.OC().callref);
	}

	return true;
}

						OnFormLoaded(strFormName, strCallclass)
{
	// TODO: Add your event handler code here

	//--TK Performance 
	if(app.oSettings.get_setting("qatime")==1)
	{
		_swdoc.endTime = new Date().getTime();
		var intTime = _swdoc.endTime - _swdoc.startTime;
		app.g.qa_log_time("Incident Details Loaded", intTime);
	}
	//--F0110887 Start
	var oRS = app.g.get_sqrecordset("company/get_expiry_date","company_id="+_swdoc.oCust().fk_company_id);
  	if (oRS.Fetch())
  	{ 
		var intExpiryEpoch = app.g.fix_epoch(app.g.get_field(oRS,"SUPPORTEXPIRYX"));
		app.g.checkOrgSupport(_swdoc.oCust().fk_company_id, intExpiryEpoch, true);
	}
	//--F0110887 End
	
}

						OnFormLoaded()
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		var strParams = "fk_workflow_id="+app.pfu(bpm_workflow.pk_workflow_id);
   		var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
		while(oRS.Fetch())
		{
			var strBinding = app.g.get_field(oRS,"fieldname");
			arrBinding = strBinding.split(".");
			_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
		}
	}//-- EOF F0105032
	
	_swdoc.setup_process_fields(false);
	if(app.g.remote_support_enabled())
	{
		app._evi(_swdoc.MF().btn_remotesupport, true);	
	}
	
    //-- enable disable fields if cannot edit
    app._een(mb_cust_actions,(app.itsm.can_edit(_swdoc.OC().callclass)));
  
	//-- store _swdoc.current probcode
	_swdoc.orig_probcode = _swdoc.opencall.probcode;
	
	_swdoc.setup_form_fields();

	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	var boolExternalCompany = (dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1);
	app._een(_swdoc.MF().lb_company, boolExternalCompany);
	if(!_swdoc.MF().lb_company.enable)
	{
		//-- check if we should hide
		app._evi(_swdoc.MF().lb_company,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));		

		//alter filter if we do not support external
		app._slf(_swdoc.MF().lb_oc_cc , "");
		app._slf(_swdoc.MF().lb_oc_site , "");
		app._slf(_swdoc.MF().fld_oc_dept , "");
	}
	app._evi(_swdoc.MF().lbl_company, _swdoc.MF().lb_company.visible);
	//###20120719
	app._evi(_swdoc.MF().ulb_company, _swdoc.MF().lb_company.visible);	
	app._evi(_swdoc.MF().lbl_c_company, _swdoc.MF().lb_company.visible);
	//###20120720	
	app._evi(_swdoc.MF().btn_change_org, _swdoc.MF().lb_company.visible);
	
	
	//-- eof hide / enable	
	TabAssess.ShowTabItem(3,app.g.sla_has_olas(_swdoc.OC().itsm_sladef));
	app._etab(TabAssess,0);
	_swdoc.strOriginalSLA = _swdoc.opencall.priority;
	_swdoc.strOrigCompany = _swdoc.oCompany().companyname;
	_swdoc.intLastOCStatus = _swdoc.OC().status;	
	
	app._ete(_swdoc.MF().lbl_oc_callref,_swdoc.OC().h_formattedcallref);
	
	var strFilterPicklist = "Currently Affected|by Customer"; // DTH: webclient - added Currently Selected
	if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AllowSiteFilterCIsAndServices")==1)
		strFilterPicklist += (!boolExternalCompany)?"|by Site":"|by Settings: Site|by Customer: Site";
	if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AllowOrganisationFilterCIsAndServices")==1)
		strFilterPicklist += (!boolExternalCompany)?"|by Organisation":"|by Settings: Organisation|by Customer: Organisation";
	if(boolExternalCompany && dd.GetGlobalParamAsNumber("Application Settings/Incident/AllowContractFilterCIsAndServices")==1 && boolExternalCompany)
		strFilterPicklist += "|by Contract";
		
	_swdoc.MF().fld_cis.pickList = strFilterPicklist;
	
	app._eva(_swdoc.MF().fld_cis, "Currently Affected"); // DTH: webclient changed from Customer to Currently Affected
	
	//-- Show or hide contract field based on whether we support external companies
	app._evi(_swdoc.MF().lbl_contract, boolExternalCompany);
	app._evi(_swdoc.MF().lb_contract, boolExternalCompany);
	//-- Filter Contract List for Selected company

	_swdoc.intCurrentMajorFlag = _swdoc.OC().itsm_majorinc;
	if(_swdoc.OC().itsm_majorinc=="1")
		app._ete(mainform.CaptionPanel7 , "Major Incident");

	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			app._een(_swdoc.MF().btn_actionsts, true);
	}
}

