						sl_cust_cis_OnItemSelected(nRow)
{
	//-- NWJ
	//-- 27.11.2006
	//-- enable list menus if an item is selected	
	var boolActive = (sl_cust_cis.curSel > -1);
	var selCount = app.g.sl_getselected_count(sl_cust_cis);
	app._een(mbtn_cci_views, (selCount==1));
	app._een(mbtn_cci_actions, boolActive);	


}

						mbtn_cci_actions_OnMenuItem(strName, nItemID)
{
	//-- NWJ
	//-- 27.11.2006
	//-- Handle actions
	
	var iAssignCI = 1;
	var iRefresh = 2;
	var iRemoteSupport = 3;
	
	//-- assign selected customer CI to this incident
	if(nItemID==iAssignCI)
	{
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true))return false;
		
		//-- Relate the CI to the call
		var pk_ci_id = sl_cust_cis.GetItemTextRaw(sl_cust_cis.curSel,'pk_auto_id');
		var ci_item_key = sl_cust_cis.GetItemTextRaw(sl_cust_cis.curSel,'ck_config_item');		
		
		//-- make sure is not already related
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems, pk_ci_id, 0) < 0)
		{
			//-- relate the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'INCIDENT'))
			{
				var strText = "Configuration Item (" + ci_item_key + ") was added to this Incident.\n"
				var strActionSource = "Incident Management";
				var strCode			= "Linking Configuration Items";
				
				app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
				
				//-- filter list again
				_swdoc.EF().sl_configitems.Refresh();
				_swdoc.uncheckset_customer_items();
			}
		}
		else
		{
			MessageBox("The selected customer configuration item is already associated to this incident");
		}
		
		return;
	}
	else if(nItemID==iRefresh)
	{
		sl_cust_cis.Refresh();
		_swdoc.uncheckset_customer_items();				
	}
	else if(nItemID==iRemoteSupport)
	{
		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var intItem = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,-1,"pk_auto_id");
			var oCI = app.g.get_record("config_itemi",intItem);
			app.g.start_pinned_remote_support(oCI, _swdoc.OC().callref);
		}	
		else
		{
			MessageBox("Remote Support is not available for this item");
		}
	}
}

