    else if(nItemID==iMgr)
    {
    	//-- popup a form 
		if (_swdoc.userdb.fk_manager != "") 
			app.OpenFormForEdit("tfCustomer", _swdoc.userdb.fk_manager, "", true);
	}
/*###20120720    else if(nItemID==iChangeCompany)
    {
		var sResults = app.g.search_for("company");
    	if(sResults.selectedkeys=="") return;
    
