*/
      
}

						sl_cust_cis_OnItemChecked(nRow, bChecked)
{
	if(!app.itsm.can_edit(_swdoc.OC().callclass,true))
	{
		sl_cust_cis.SetRowChecked(nRow, !bChecked);
		return false;
	}


	var pk_ci_id = sl_cust_cis.GetItemTextRaw(nRow,"pk_auto_id");		
	var ci_item_key = sl_cust_cis.GetItemTextRaw(nRow,"ck_config_item");
	var ci_item_type = sl_cust_cis.GetItemTextRaw(nRow,"ck_config_type");	
	
	
	if (pk_ci_id && ci_item_key && ci_item_type)
	{
		var strTitle = "Configuration Item";
		var strTitlePlural = "Configuration Items";
		var oList = _swdoc.EF().sl_configitems;
		app._slf(oList, "cr="+_swdoc.OC().callref+"&code=INCIDENT&type=1");
	
		// -- SG, 02/07/2012, F0103324, required variable and array creation
		var itemType = "Configuration Item";
	
		if(ci_item_type.indexOf("Service")>-1)
		{
			var strTitle = "Service";
			var strTitlePlural = "Services";
			var oList = _swdoc.EF().sl_services;
			app._slf(oList, "cr="+_swdoc.OC().callref+"&code=INCIDENT&type=2");

			// -- SG, 02/07/2012, F0103324, required variable and array creation
			var itemType = "Service";
		}

		if(bChecked)		
		{
			//-- Relate the CI to the call
		
			//-- make sure is not already related
			if(app.g.sl_findrow_byvalue(oList, pk_ci_id, "pk_auto_id") < 0)
			{
				//-- relate the call
				if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'INCIDENT'))
				{
				var strText = strTitle+" (" + ci_item_key + ") was added to this Incident.\n"
				var strActionSource = "Incident Management";
				var strCode			= "Linking "+strTitlePlural;
				
				app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
				
				//-- filter list again
				oList.Refresh();			
				}
			_swdoc.check_probprofile_against_problems_kes();
			}
			else
			{
			MessageBox("The selected customer "+strTitle+" is already associated to this incident");
			}
		
		// -- SG, 03/07/2012, F0103324
		// Create new array to hold CI and Bespoke Terms info
		bespokeTermsArray = new Array();
		var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    		bespokeTermsArray[0] = new Array(2);
    		bespokeTermsArray[0][0] = rec.ck_config_item;
    		bespokeTermsArray[0][1] = rec.recordresolvedmsg;
			}		
		app.bespokeTermsOutput(bespokeTermsArray, 1, itemType);
		// -- END OF BESPOKE TERMS PROCESSING
		}	
		else
		{
		//-- remove
   		//-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
   			if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,pk_ci_id,'INCIDENT'))
   			{
   			//-- update incident diary
   			var strCIidentifiers = ci_item_key; //--app.g.sl_getselected_valuesdel(sl_configitems,1,",");
			var strText     	= "The following "+strTitlePlural+" were removed from this Incident.\n" + strCIidentifiers;
			var strActionSource = "Incident Management";
			var strCode			= "Remove "+strTitlePlural;
			app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
   			}			

	    //-- re-filter list	
		oList.Refresh();	
		_swdoc.check_probprofile_against_problems_kes();
		}	
	
	}

}

						mbtn_serv_actions_OnMenuItem(strName, nItemID)
{
	//-- NWJ
	//-- 27.11.2006
	//-- Handle actions
	
	var iAssignCI = 1;
	var iRefresh=2;
	
	//-- assign selected customer CI to this incident
	
	if(nItemID==iAssignCI)
	{
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true))return false;
		
		//-- Relate the CI to the call
		var pk_ci_id = sl_cust_services.GetItemTextRaw(sl_cust_services.curSel,'pk_auto_id');
		var ci_item_key = sl_cust_services.GetItemTextRaw(sl_cust_services.curSel,'ck_config_item');		
		
		//-- make sure is not already related
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_services, pk_ci_id, 0) < 0)
		{
			//-- relate the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'INCIDENT'))
			{
				var strText = "Service (" + ci_item_key + ") was added to this Incident.\n"
				var strActionSource = "Incident Management";
				var strCode			= "Linking Services";
				
				app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
				
				//-- filter list again
				var strAddFilter = "(CK_CONFIG_TYPE LIKE 'ME->SERVICE' OR CK_CONFIG_TYPE LIKE 'Service%')";
				app.cmdb.sl_load_call_cis(_swdoc.EF().sl_services, _swdoc.OC().callref ,"INCIDENT",strAddFilter);			
				_swdoc.uncheckset_customer_items();				
			}
		}
		else
		{
			MessageBox("The selected customer configuration item is already associated to this incident");
		}
		
		return;
	}
	else if(nItemID==iRefresh)
	{
		sl_cust_services.Refresh();
		_swdoc.uncheckset_customer_items();				
	}
}

						mbtn_profile_OnMenuItem(strName, nItemID)
{
	var iDesc = 2;
	var iProfile = 1;
	var iSumm=4;
	var iAPR=3;
	
	if(nItemID==iDesc)
	{
  		//if(_swdoc.updatedb.updatetxt == "")
		if(_swdoc.opencall.prob_text =="")  		
  		{
        	MessageBox("Please enter some descriptive text before trying to perform a Knowledge base search.");
        }
    	else
    	{
    		if(!app.g.use_sw_knowledgebase())
			{
				app.allocatekbdoc(app.session.analystId,_swdoc);
        		app.g.open_third_party_kb_search(true, _swdoc.opencall.prob_text, _swdoc.OC().callref,app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,","), _swdoc._kbidentifier);
			}
			else
			{ 		
        		url = "hsl:KbSearch?search=" + _swdoc.opencall.prob_text;
        		app.global.OpenEmbeddedURL(url);
        	}
    	}
    }
    else if(nItemID==iSumm)
	{
  		if(_swdoc.opencall.itsm_title == "")
  		{
        	MessageBox("Please enter a summary before trying to perform a Knowledge base search.");
        }
    	else
    	{   	
    		if(!app.g.use_sw_knowledgebase())
			{
				app.allocatekbdoc(_swdoc._kbidentifier,_swdoc);
        		app.g.open_third_party_kb_search(true, _swdoc.opencall.itsm_title, _swdoc.OC().callref,app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,","), _swdoc._kbidentifier);
			}
			else
			{ 			
        		url = "hsl:KbSearch?search=" + _swdoc.opencall.itsm_title;
        		app.global.OpenEmbeddedURL(url);
        	}
    	}
    }
	else if(nItemID==iProfile)
	{
  		if(_swdoc.OC().probcode == "")
  		{
        	MessageBox("Please select an incident profile before trying to perform a Knowledge base search.");
        }
    	else
    	{
    		if(!app.g.use_sw_knowledgebase())
			{
				MessageBox("This option is not available with your Knowledgebase.");
			}
			else
			{ 		
        		url = "hsl:KbSearch?search=" + _swdoc.OC().probcode;
        		app.global.OpenEmbeddedURL(url);
        	}
    	}
    	return;
    }
	else if(nItemID==iAPR)
	{
		//-- Auto problem search
  		if(_swdoc.OC().probcode == "")
  		{
        	MessageBox("Please select an incident profile before trying to perform a Problem Recognition search.");
        }
		else
		{	
			app.g.search_for("apr",true,"matchingprobcode=" + _swdoc.OC().probcode,function(oResult)
			{
				if(oResult.selectedkeys!="")
				{	
					_swdoc.link_incident_problems(oResult.selectedkeys, oResult.selectedother);	
				}
			});
		}
	}
}

						mb_inc_actions_OnMenuItem(strName, nItemID)
{
	var iProblem = 1;
	var iRFC = 2;
	var iSR = 3;
	//-- F0105872 - ensure original description is loaded
	_swdoc.load_diary_orig();
	var iChangeCompany = 5;
	
	if(nItemID==iProblem)
	{
		//-- NWJ rel2.1 CR21 - raise problem from incident
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.OC();
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].lastupdatetxt = tb_ongoing.text;
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = tb_origtext.text
		
		//-- open new problem record
		app.global.LogNewCall("Problem");
    }
	else if(nItemID==iRFC)
	{
		//-- NWJ rel2.1 CR21 - raise problem from incident
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.OC();
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].lastupdatetxt = tb_ongoing.text;
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = tb_origtext.text
		
		//-- open new problem record
		app.global.LogNewCall("Change Request");
    }else if(nItemID==iSR)
	{
		//-- RJC Merope - raise app.service request from incident
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.OC();
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].lastupdatetxt = tb_ongoing.text;
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = tb_origtext.text
		
		//-- open new problem record
		app.global.LogNewCall("Service Request");
    } else if(nItemID==iChangeCompany) {
    
    	if(!app.itsm.can_edit(_swdoc.OC().callclass)) return

    	if(dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)
    	{
    		//### also used by button next to Settings/Opencall Company field!
			//###20120802 var intQResult = MessageBox("By changing the Organisation the value of Site Name, Charge Centre, Department and Contract will reset.\nAre you sure you want to continue?", MB_YESNOCANCEL|MB_ICONQUESTION);
			//###20120802 if (2 == intQResult) return; //2 is Cancel, 6 = Yes
			//###20120802 if (6 != intQResult)
			MessageBox("The value for Site Name, Charge Centre, Department and Contract will remain, however the dropdown list will be re-populated.", MB_OKCANCEL|MB_ICONQUESTION, function(intQResult)
			{
				//###20120802 _swdoc.boolEmptySites = (6 == intQResult || 2 != intQResult);
				if (2 == intQResult) return;
				_swdoc.boolEmptySites = false;
				app.g.search_for("company",true,"",function(sResults)
				{
					if(sResults.selectedkeys=="") return;
    		
    				var arrKey = sResults.selectedkeys.split(",");
    				_swdoc.ChangeRecord("company", arrKey[0]);
				});
			});
    		
    	}
    	else
    	{
    		MessageBox("This feature is not accessible as the option to Support External Companies is not enabled.");
    	}
    }  

    
}

						mbtn_cci_views_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_cust_cis,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_cust_cis,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}	
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

