						mb_ots1_OnMenuItem(strName, nItemID)
{
	var iView = 1;
	var iUpdate = 2;
	var iAssign = 3;
	var iHold = 4;
	var iOffHold = 5;
	var iResolveClose = 6;	
	var iCancel = 7;
	var iNew = 8;		

	var intCallref = sl_ots1.GetItemTextRaw(sl_ots1.curSel, 'callref');
	var intStatus = sl_ots1.GetItemTextRaw(sl_ots1.curSel, 'status');
	//-- F0097545
	if(intCallref=="")
		return;

	switch(nItemID)
	{
		case iView:
			app.global.OpenCallDetailsView(intCallref);
			break;
		case iNew:
			app.g.LOG_FROM_CALL = _swdoc.opencall;
			app.global.LogNewCall("OLA Task");
			break;
			
		case iUpdate:
			
			var strArgs = "in_callrefs=" + intCallref;
			app.OpenForm("itsmEfUpdateCallForm_OLT", strArgs, true);
			//app.OpenForm("itsmEfUpdateCallForm", strArgs, true);
			break;			
		case iHold:		
			if(intStatus==1)
			{
				var strArgs = "in_callrefs=" + intCallref;
				app.OpenForm("itsmEfHoldCallForm_OLT", strArgs, true);
			}
			else
			{
				MessageBox('Cannot place task on hold until it is accepted.');
			}
			break;			
		case iOffHold:
			if(intStatus==4)
			{
				var xmlmc = new XmlMethodCall;
 
				// Set values for the Input parameters - Parameter values must be set in the same order they are expected by the method.
				xmlmc.SetParam("callref", intCallref); 				
				
				// Invoke the VPME method LogCall.
				if(xmlmc.Invoke("VPME", "offHold"))
				{
    				// Success, lets access output parameters
    				MessageBox("Task successfully taken off hold.");
				}
				else
				{
    				// Error
    				MessageBox(xmlmc.GetLastError());
				}

			}
			else
			{
				MessageBox('Task is not on hold.');
			}
			break;			
		case iResolveClose:		
			if(intStatus==1)
			{
			var strArgs = "in_callrefs=" + intCallref;
			app.OpenForm("itsmEfResolveCloseCallForm_OLT", strArgs, true);
			}
			else
			{
				MessageBox('Cannot resolve / close task until it is accepted.');
			}
			break;			
			
		case iCancel: 
			var strArgs = "callrefs=" + intCallref;
			app.OpenForm("EfCancelCallForm", strArgs, true);
			break; 
					
	}
	

}

