						mbtn_sla_view_OnMenuItem(strName, nItemID)
{
	//-- F0091281
	if(!app.session.HaveRight(ANALYST_RIGHT_C_GROUP,ANALYST_RIGHT_C_CANMANAGESLAS))
	{
		app.global.MessageBox('Insufficient privileges to perform operation.\n\nYou have been denied the: [Can manage SLAs] permission.\n\nPlease contact your Supportworks system administrator', 16);
		return false;	
	}
	
	var iPriority = 1;
	var iSLA = 2;	
	
	if(nItemID == iPriority)
	{
		var strParams = "nm="+app.pfu(_swdoc.OC().priority);
		var oSLA = app.g.get_sqrecordset("call/common/get_priority_details",strParams); 
		if(oSLA.Fetch())
		{
			var colValue = app.g.get_field(oSLA,"slaid");
			app.global.InvokeSlaEditDialog(colValue, false,"Priority");
		}
		return;
    }
	else if(nItemID == iSLA)
	{
		app.OpenFormForEdit("itsmsp_slad", _swdoc.OC().itsm_sladef, "", true, function(){
			return;
		});
	}
	
}

