						OnTimer(nTimer)
{
	// TODO: Add your event handler code here

	_form.KillTimer(nTimer);
	//-- used to handle form tab refresh on bpm state change
	if(nTimer==5005)_swdoc.setup_process_fields(true);	
	if(nTimer==1001)
	{
		var progress = _swdoc.OC().bpm_progress;
		var i = 0;
		var check = true;
		if(_swdoc.OC().bpm_progress_fail >= 1)
		{
			_swdoc.MF().si_progress1.colour = "#CC0000";
		}
		else
		{
			_swdoc.MF().si_progress1.colour = "#000000";
		}
		if(progress < 1)
		{
			check = false;
		}
		while(_swdoc.MF().si_progress1.GetItemText(i, 1))
		{
			_swdoc.MF().si_progress1.SetRowChecked(i, check)
			if(_swdoc.MF().si_progress1.GetItemText(i, 1) == progress)
			{
				check = false;
			}
			i++;
		}
	}
		
	if(nTimer=1)
	{
		_swdoc.uncheckset_customer_items();
	}
		
}

						TabAssess_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	// TODO: Add your event handler code here
	var tProgress=7;
	var tTasks = 6;
	// DTH: webclient - optimising the above to a switch statement AND applying filters to SQL Lists on selection of a tab
	var tServiceLevel = 1;
	var tOlaTasks = 3;
	var tProcessStage = 4;
	var tAuthorisations = 5;
	var tBpmTasks = 6;
	switch (nTab)
	{
	
		case tServiceLevel:
			//-- Filter Contract List for Selected company
			//###20120730 app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract, _swdoc.OC().itsm_sladef);
			app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract, _swdoc.OC().itsm_sladef);
		break;
		
		case tOlaTasks:
			
				
			// DTH: webclient - set the filter for the SQL list on this page. AutoLoad set to FALSE
			app._slf(_swdoc.MF().sl_ots1, "cr="+ _swdoc.OC().callref);
			//--TK Refresh Needed
			_swdoc.MF().sl_ots1.Refresh();
			if(sl_ots1.rowCount()>0)
				app._een(mainform.btn_view, true);
		break;
		
		case tProcessStage:
			if(_swdoc.OC().bpm_workflow_id!="")
			{
				var intCount = app.g.sqs_rowcount("call/common/cnt_progress_items","wf="+app.pfu(_swdoc.OC().bpm_workflow_id));
				if(intCount>0)
					app._een(mainform.btn_bpm_progress1, true);
			}
		break;
		
		case tAuthorisations:
			// DTH: webclient - set the filter for the SQL list on this page. AutoLoad set to FALSE
			app._slf(_swdoc.MF().sl_wfauth1, "cr="+ _swdoc.OC().callref +"&sid="+ _swdoc.OC().bpm_stage_id);
			_swdoc.MF().sl_wfauth1.Refresh();
		break;
		
		case tBpmTasks:
			// DTH: webclient - set the filter for the SQL list on this page. AutoLoad set to FALSE
			app._slf(_swdoc.MF().sl_tasks, "cr="+ _swdoc.OC().callref +"&sid="+ _swdoc.OC().bpm_stage_id);
			_swdoc.boolRefreshTasks = true;
			_swdoc.MF().sl_tasks.Refresh();
		break;
		
		case tProgress:
			// mainform.SetTimer(1001, 10); // DTH: webclient This tab is not displayed so there is no need to control it's properties
		break;
	}


}

