	_swdoc.opencall.environment = strValue;
}

						btn_search_ci_OnPressed()
{
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

    //-- NWJ 08.03.2004
    //-- open CI search form in select mode
    var strURL = "canreset=1&fk_company_id=" + app.pfu(_swdoc.oCust().fk_company_id);
	app.cmdb.search_ci(true,strURL,function(sResults)
	{
		if (sResults.selectedkeys=="") return;
 
		//-- loop through selected CIs
		var strText = "";
		var strServiceText = "";
		var arrayCIkeys = sResults.selectedkeys.split(",");
		var arrayCItext = sResults.selectedtext.split(",");	
		var arrayCItypes = sResults.selectedtypes.split(",");	
		
		// -- SG, 02/07/2012, F0103324
    	// Start array counter, create new array to hold CI and Bespoke Terms info
		var countBespokeTerms = 0;
		bespokeTermsArray = new Array();
		// End of array and var creation
		
		for (var x=0;x<arrayCIkeys.length;x++)
    	{
        	var pk_ci_id = arrayCIkeys[x];
        	var ci_item_text = arrayCItext[x];
        	var ci_type_text = arrayCItypes[x];
        	
        	// -- SG, 02/07/2012, F0103324
        	// Build array of CI's that have the Bespoke Terms field set
        	var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			} 
        	// End of array building
        	
 			if(ci_type_text.indexOf("Service")>-1)
  			{
 				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_services,pk_ci_id,0);
				if (rowPos == -1)
				{ 
					//-- Relate the CI to the call
					if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'INCIDENT'))
					{
						if(strServiceText!="")strServiceText += ",";
						strServiceText += ci_item_text;
					}
				}
  			}else
  			{
			
				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_configitems,pk_ci_id,0);
				if (rowPos == -1)
				{ 
					//-- Relate the CI to the call
					if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'INCIDENT'))
					{
						if(strText!="")strText += ",";
						strText += ci_item_text;
					}
				}
			}
		}   
	
		//-- apply call diary update for added CIs (if we have any to add)
		if (strText!="")
		{
			strText = "The following configuration items have been associated to this incident.\n" + strText;
			var strActionSource = "Incident Management";
			var strCode			= "Linking Configuration Items";
			
			app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
			sl_configitems.Refresh();
		}
			//-- apply call diary update for added CIs (if we have any to add)
		if (strServiceText!="")
		{
			strServiceText = "The following services have been associated to this incident.\n" + strServiceText;
			var strActionSource = "Incident Management";
			var strCode			= "Linking Services";
			
			app.g.new_dairyevent(_swdoc.OC().callref,strServiceText,strActionSource,strCode);
			sl_services.Refresh();
		}
	
		// -- SG, 02/07/2012, F0103324
    	app.bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, "Configuration Item");
		
		// DTH: webclient to refresh the list on the details tab
		_swdoc.load_cis();	
	});
}

						mbtn_srch_prb_OnMenuItem(strName, nItemID)
{
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

	var iNormal = 1;
	var iAPR = 2;
	var iCIs = 3;

	switch(nItemID)
	{
    	case iNormal:
   			var strURL = "canreset=1";    	
    		app.itsm.search_calls("PRB", strURL,true,function(sResults)
    		{
    			if(sResults.selectedkeys=="")
    			{
    				return;
    			}
    			else
    			{
    				//-- if searching add stuff
					_swdoc.link_incident_problems(sResults.selectedkeys, sResults.selectedother);
    			}	
    		});
			break;
    	case iAPR:
   			app.g.search_for("apr",true,"",function(sResults)
   			{
   				if(sResults.selectedkeys=="")
   				{
   					return;
   				}
   				else
   				{
   					//-- if searching add stuff
					_swdoc.link_incident_problems(sResults.selectedkeys, sResults.selectedother);
   				}
   			});
   			break;
    					
	   	case iCIs:	
		{	
			var strCIKeys = app.g.sqllist_get_valuesdel(sl_configitems, 'pk_auto_id', ",", "");
			var strServiceKeys = app.g.sqllist_get_valuesdel(sl_services, 'pk_auto_id', ",", "");
			if(strServiceKeys!="")
			{
				if(strCIKeys!="")strCIKeys+=",";
				strCIKeys+=strServiceKeys;
			}
  			var strURL = "canreset=1";
  			if(strCIKeys!="")
  			{
  				strURL += "&ci_keys="+strCIKeys;
  			}
    		app.itsm.search_calls("PRB",strURL,true,function(sResults)
    		{
    			if(sResults.selectedkeys=="")
  	 			{
  	 				return;
  	 			}
  	 			else
  	 			{
  	 				//-- if searching add stuff
					_swdoc.link_incident_problems(sResults.selectedkeys, sResults.selectedother);
  	 			}
    		});
    		break;
  	 	} 			
	}
}

						btn_srch_rfc_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

   	var strURL = "canreset=1";    	
    app.itsm.search_calls("RFC",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;
   
		var array_rfc = oResults.selectedkeys.split(",");  
		var array_classes = oResults.selectedclasses.split(",");
 		var strText = "This incident has been associated with the following Change Requests\n\n";
		var boolRFCs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_rfc.length;x++)
    	{
        	var rfc_callref = array_rfc[x];
        	var rfc_callref_text = app.g.pad_callref(rfc_callref);
			//-- check that rfc is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_rfcs,rfc_callref,"callref");
			if (rowPos == -1)
			{ 
				boolRFCs = true;		
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(rfc_callref,_swdoc.OC().callref,'RFC-INCIDENT'))
				{
					strText += "Change Request" + " (" + rfc_callref_text + ") associated\n";
					
					//-- update rfc diary
					strRfcText = "The incident (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this Change Request";
					app.g.new_diaryevent(rfc_callref,strRfcText,"Incident Management","Incident Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolRFCs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Change Association");
			sl_rfcs.Refresh();
		}
    });
}

						mbtn_rfc_actions_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;
	
	//-- NWJ - unlink rfc
	if(nItemID == iDisassociate)
	{
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_rfcs,"callref",",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs,_swdoc.OC().callref,'RFC-INCIDENT'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This incident has been dis-associated from the following Change Requests\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Change Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRFCText = "The incident (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this Change Request";
					app.g.new_diaryevent(arrCallrefs[x], strRFCText,"Incident Management","Incident Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Change Request Dis-association");
				sl_rfcs.Refresh();
			}
		
			

		}	
	}

}

						mbtn_ici_actions_OnMenuItem(strName, nItemID)
{

	var iRemove = 1;	
	var iRemoteSupport = 2;
	if(nItemID==iRemove)
	{
		//-- remove CI
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true)) return;
	
      	//-- NWJ - 15.03.2004
      	//-- Remove Selected config items
		var strCIs = app.g.sl_getselected_valuesdel(sl_configitems,0,",");
		if(strCIs!= "")
		{
    		//-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
    		if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,strCIs,'INCIDENT'))
    		{
    			//-- update incident diary
    			var strCIidentifiers = app.g.sl_getselected_valuesdel(sl_configitems,1,",");
				var strText     	= "The following cConfiguration items were removed from this Incident.\n" + strCIidentifiers;
				var strActionSource = "Incident Management";
				var strCode			= "Remove Configuration Items";
				app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
    		}			

	    	//-- re-filter list	
			sl_configitems.Refresh();
			// DTH: webclient to refresh the list on the details tab
			//uncheckset_customer_items();	Not required as it is called from Load_cis()
			_swdoc.load_cis();		
      	}
	}

	if(nItemID==iRemoteSupport)
	{
		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var intItem = app.g.sl_getrawcolvalue(sl_configitems,-1,"pk_auto_id");
			var oCI = app.g.get_record("config_itemi",intItem);
			app.g.start_pinned_remote_support(oCI, _swdoc.OC().callref);
		}	
		else
		{
			MessageBox("Remote Support is not available for this item");
		}
	}

}

						mbtn_ici_views_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;	
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_configitems,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_configitems,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

