						TabRel_OnTabSelected(nTab)
{
	//-- NWJ - 28.11.2006
	//-- Handle tab clicking
	tCMDB = 0;
	tServices = 1;
	tProblems = 2;
	tRFCs = 3;
	tSRs = 4;
	tKB = 5;
	tIncidents = 6;
	tCauses = 7;
	tMajIncidents = 8;	
	tChildIncidents = 9;
	tRAKb = 10;
	
	if(nTab==tCMDB)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_configitems.rowCount() < 1) sl_configitems.Refresh();
	}
	if(nTab==tProblems)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_problems.rowCount() < 1) sl_problems.Refresh();
	}
	else if(nTab==tRFCs)
	{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (sl_rfcs.rowCount() < 1) sl_rfcs.Refresh();
	}
	else if(nTab==tSRs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_srs.rowCount() < 1) sl_srs.Refresh();
	}
	else if(nTab==tKB)
	{
		if (sl_kbs1.rowCount() < 1)
		{
			app._slf(sl_kbs1,"cr="+_swdoc.OC().callref);	
			sl_kbs1.Refresh();
		}
	}	
	else if(nTab==tIncidents)
	{
		_swdoc.bool_slcustincs_displayed=true;
		Tab1_OnTabSelected(Tab1.tab);
	}
	else if(nTab==tMajIncidents)
	{
		if (sl_majinc.rowCount() < 1)sl_majinc.Refresh();
	}	
	else if(nTab==tChildIncidents)
	{
		if (sl_assinc.rowCount() < 1)sl_assinc.Refresh();
	}	
	else if(nTab==tServices)
	{
		if (sl_services.rowCount() < 1)
			sl_services.Refresh();
		// DTH: webclient
		_swdoc.boolAffectedServicesLoaded = true;
	}else if(nTab==tCauses)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_rels1.rowCount() < 1) sl_rels1.Refresh();
	}
	else if(nTab==tRAKb)
	{
		if (sl_rakb.rowCount() < 1)
		{
			app._slf(sl_rakb,"cr="+_swdoc.OC().callref);	
			sl_rakb.Refresh();
		}
	}		

}

						OnFormLoaded(bDataReloaded)
{
	//-- NWJ - 28.02.2006
	//--       Use new app.cmdb object to get sqllist filter
	
	if (_swdoc.strOrigCallCis == "")
		_swdoc.strOrigCallCis = app.cmdb.get_call_cis(_swdoc.OC().callref, "INCIDENT", "");
		
	app._slf(_swdoc.EF().sl_configitems, "cr="+_swdoc.OC().callref+"&code=INCIDENT&type=1");
	_swdoc.EF().sl_configitems.Refresh();
	
	_swdoc.hideshow_majorincstab();
	if(IsObjectDefined("mainform.sl_cust_services"))
		_swdoc.uncheckset_customer_items();
		
	//-- Show appropriate KB Tab
	var tTab = _swdoc.EF().TabRel.tab;
	if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			_swdoc.EF().TabRel.ShowTabItem(10,true);
			_swdoc.EF().TabRel.ShowTabItem(5,false);
			app._etab(_swdoc.EF().TabRel, tTab);
		}
	}
	else
	{
		_swdoc.EF().TabRel.ShowTabItem(10,false);
		_swdoc.EF().TabRel.ShowTabItem(5,true);
		app._etab(_swdoc.EF().TabRel, tTab);
	}
}

