						mbtn_prb_actions_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;
	var iRefresh = 2;
	
	//-- NWJ 02.11.2006 - link problems and kes
	if(nItemID == iDisassociate)
	{
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_problems,"callref",",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs,_swdoc.OC().callref,'PROBLEM-INCIDENT'))
			{
				var arrCallClasses = app.g.sl_getselected_valuesdel(sl_problems,1,",").split(',');
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This incident has been dis-associated from the following Problems and Known Errors\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += arrCallClasses[x] + " (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					//-- update ke or problem diary
					strPrbText = "The incident (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been dis-associated from this " + arrCallClasses[x];
					app.g.new_diaryevent(arrCallrefs[x],strPrbText,"Incident Management","Incident Dis-association");
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Problem Dis-association");
			}
			
			sl_problems.Refresh();
		}
		return;
	}


}

