						btn_srch_inc_OnPressed()
{
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

   	var strURL = "canreset=1";    	
    app.itsm.search_calls("INC", strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;
   
		var array_inc = oResults.selectedkeys.split(",");  
		var array_classes = oResults.selectedclasses.split(",");
 		var strText = "This major incident has been associated with the following incidents\n\n";
		var boolINCs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_inc.length;x++)
    	{
        	var inc_callref = array_inc[x];
        	var inc_callref_text = app.g.pad_callref(inc_callref);
        	if(inc_callref!=_swdoc.OC().callref)
        	{
				//-- check that inc is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_assinc,inc_callref,"callref");
				if (rowPos == -1)
				{ 
					boolINCs = true;		
					//-- call app.g method to link problem to incident
					if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'MINC-INCIDENT'))
					{
						strText += "Incident" + " (" + inc_callref_text + ") associated\n";
						
						//-- update rfc diary
						strIncText = "This incident has been associated to the major incident (" + app.g.pad_callref(_swdoc.OC().callref) + ")";
						app.g.new_diaryevent(inc_callref,strIncText,"Incident Management","Major Incident Association");
					}
				}
			}
			else
			{
				MessageBox("This incident cannot be associated as a child of itself");
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolINCs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Major Incident Management","Incident Association");
			sl_assinc.Refresh();
		}
    });
}

