						btn_srch_srs_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

   	var strURL = "canreset=1";    	
    app.itsm.search_calls("SR",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;
   
		var array_sr = oResults.selectedkeys.split(",");  
		var array_classes = oResults.selectedclasses.split(",");
 		var strText = "This incident has been associated with the following Service Requests\n\n";
		var boolSRs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_sr.length;x++)
    	{
        	var sr_callref = array_sr[x];
        	var sr_callref_text = app.g.pad_callref(sr_callref);
			//-- check that rfc is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_srs,sr_callref,"callref");
			if (rowPos == -1)
			{ 
				boolSRs = true;		
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(sr_callref,_swdoc.OC().callref,'REQUEST-INCIDENT'))
				{
					strText += "Service Request (" + sr_callref_text + ") associated\n";
					
					//-- update rfc diary
					strSRText = "The incident (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this Service Request";
					app.g.new_diaryevent(sr_callref,strSRText,"Incident Management","Incident Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolSRs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Service Request Association");
			sl_srs.Refresh();
		}
    });
}

