						mbtn_ici_views1_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;	
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_services,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_services,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_ici_actions1_OnMenuItem(strName, nItemID)
{

	var iRemove = 1;	
	if(nItemID==iRemove)
	{
		//-- remove CI
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true)) return;
	
      	//-- NWJ - 15.03.2004
      	//-- Remove Selected config items
		var strCIs = app.g.sl_getselected_valuesdel(sl_services,0,",");
		if(strCIs!= "")
		{
    		//-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
    		if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,strCIs,'INCIDENT'))
    		{
    			//-- update incident diary
    			var strCIidentifiers = app.g.sl_getselected_valuesdel(sl_services,1,",");
				var strText     	= "The following Services were removed from this Incident.\n" + strCIidentifiers;
				var strActionSource = "Incident Management";
				var strCode			= "Remove Services";
				app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
    		}			

	    	//-- re-filter list	
			sl_services.Refresh();
			
			// DTH: webclient to refresh the list on the details tab
			//uncheckset_customer_items();	Notr required as it is called from Load_cis()
			_swdoc.load_cis();		
      	}
	}


}

						btn_search_ci1_OnPressed()
{
	
	//-- JPG 22.01.2015
    //-- open CI search form in select mode
    // It is redefined the mode for the new window
	var strURL = "searchmode=1&multiselect=1&canreset=1&fk_company_id=" + app.pfu(_swdoc.oCust().fk_company_id);
	
	// It is defined the next window to execute, searching services with better filters
	app.g.popup("manage_portfolio", strURL,function(sResults)
	{
		// Case there are not any selection go back
    	if (sResults)
    	{
    		if (sResults.document.strSelectedKeys=="") return;  
    		
    		
    		//-- JPG 22.01.2015
			//-- loop through selected CIs
			var strText = "";
			var arrayCIkeys = sResults.document.strSelectedKeys.split(",");
			var arrayCItext = sResults.document.strSelectedText.split(",");	
		
			// -- SG, 02/07/2012, F0103324
			// Start array counter, create new array to hold CI and Bespoke Terms info
			var countBespokeTerms = 0;
			bespokeTermsArray = new Array();
			
			for (var x=0;x<arrayCIkeys.length;x++)
    		{
        		var pk_ci_id = arrayCIkeys[x];
        		var ci_item_text = arrayCItext[x];
        		
				// -- SG, 02/07/2012, F0103324
				// Build array of CI's that have the Bespoke Terms field populated
				var rec = app.g.get_record("config_itemi", pk_ci_id);
				if(rec.recordresolvedmsg != '')
				{
    				bespokeTermsArray[countBespokeTerms] = new Array(2);
    				bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    				bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    				countBespokeTerms++;
				}	
				// End of array building
					
				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_services,pk_ci_id,0);
				if (rowPos == -1)
				{ 
					//-- Relate the CI to the call
					if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'INCIDENT'))
					{
						if(strText!="")strText += ",";
						strText += ci_item_text;
					}
				}
			}   
		
			//-- apply call diary update for added CIs (if we have any to add)
			if (strText!="")
			{
				strText = "The following services have been associated to this incident.\n" + strText;
				var strActionSource = "Incident Management";
				var strCode			= "Linking Services";
				
				app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
				sl_services.Refresh();
			}
			
			// -- SG, 02/07/2012, F0103324
			app.bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, "Service");
			
			// DTH: webclient to refresh the list on the details tab
			_swdoc.load_cis();
		}
		else
		{
			return;
		}
	});
}

