						mbtn_cinc_actions1_OnMenuItem(strName, nItemID)
{
	var iRemove = 1;
	if(iRemove)
	{
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_majinc,"callref",",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs, _swdoc.OC().callref, 'MINC-INCIDENT'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This incident has been dis-associated from the following major incidents\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Major Incident (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRFCText = "This major incident was dis-associated with the incident (" + app.g.callref_pad(_swdoc.OC().callref) + ")";
					app.g.new_diaryevent(arrCallrefs[x], strRFCText,"Incident Management","Incident Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Major Incident Management","Incident Dis-association");
				sl_majinc.Refresh();
			}
		}	
	
	}


}

						btn_srch_inc1_OnPressed()
{
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

   	var strURL = "canreset=1";    	
   	app.itsm.search_calls("MAJINC", strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;
   
		var array_inc = oResults.selectedkeys.split(",");  
		var array_classes = oResults.selectedclasses.split(",");
 		var strText = "This incident has been associated with the following major incidents\n\n";
		var boolINCs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_inc.length;x++)
    	{
        	var inc_callref = array_inc[x];
        	var inc_callref_text = app.g.pad_callref(inc_callref);
        	if(inc_callref!=_swdoc.OC().callref)
        	{
				//-- check that inc is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_majinc,inc_callref,"callref");
				if (rowPos == -1)
				{ 
					boolINCs = true;		
					//-- call app.g method to link problem to incident
					if(app.g.relate_calls(inc_callref,_swdoc.OC().callref,'MINC-INCIDENT'))
					{
						strText += "Major Incident" + " (" + inc_callref_text + ") associated\n";
						
						//-- update rfc diary
						strIncText = "This major incident has been associated to the incident (" + app.g.pad_callref(_swdoc.OC().callref) + ")";
						app.g.new_diaryevent(inc_callref,strIncText,"Incident Management","Incident Association");
					}
				}
			}
			else
			{
				MessageBox("This major incident cannot be associated with itself.");		
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolINCs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Major Incident Association");
			sl_majinc.Refresh();
		}
    });
}

