    });
}

						sl_relincs_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here
	var boolActive = (sl_relincs.rowCount() > 0);
	var selCount = app.g.sl_getselected_count(sl_relincs);			
	app._een(mbtn_cinc_actions2, boolActive);	
	app._een(mbtn_inc_views, (selCount==1));


}

