	var selCount = app.g.sl_getselected_count(sl_kbs1);		
	app._een(mbtn_inc_views3, (selCount==1));
	app._een(mbtn_inc_actions3, boolActive);	


}

						mbtn_inc_views3_OnMenuItem(strName, nItemID)
{
	var strKeyValue = app.g.sl_getcolvalue(sl_kbs1, sl_kbs1.curSel, "docref");
	app.oKB.open_article_form(strKeyValue);
}

						mbtn_inc_actions3_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	//-- NWJ - unlink incident
	if(nItemID == iDisassociate)
	{
		
		var strText = "This Incident has been dis-associated with the following KB Documents\n\n";
		var strDocs = app.g.sl_getselected_valuesdel(sl_kbs1,0,",");
		if(strDocs!= "")
		{
			app.oKB.unrelate_calls_from_kb(strDocs,_swdoc.OC().callref);
			sl_kbs1.Refresh();
			strText += "Knowledgebase Document (" + strDocs + ") dis-associated\n";
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Knowledgebase Document Dis-association");
		}	
	}	
}

						btn_search_inc3_OnPressed()
{
    app.g.search_for("swkb", true,"",function(oRes)
    {
    	var strKeys = oRes.selectedkeys;
		if(strKeys=="")return;
		var arrKeys = strKeys.split(",");
 		var strText = "This Incident has been associated with the following Knowledgebase Documents\n\n";
		for (var x=0; x< arrKeys.length;x++)
		{
			var fk_key = arrKeys[x];
			if(app.g.sl_findrow_byvalue(sl_kbs1,fk_key,"docref")==-1)
			{
				app.oKB.relate_call_to_kb(fk_key,_swdoc.OC().callref,'Used');
				sl_kbs1.Refresh();
				strText += "Knowledgebase Document (" + fk_key + ") associated\n";
			}
		}			
		app.g.new_diaryevent(_swdoc.OC().callref ,strText,"Incident Management","Knowledgebase Document Association");
	
		sl_kbs1.Refresh();
		sl_kbs1.SetRowSelected(0);
		sl_kbs1_OnItemSelected(0);
		return;
    });
}

						Tab1_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here

	var tCustomer = 0;
	var tSite = 1;
	var tOrganisation = 2;
	var tRelateds=3;
	
	if (nTab == tCustomer)
	{
		app._slf(_swdoc.EF().sl_cust_incidents , "callref="+_swdoc.OC().callref+"&cust_id="+_swdoc.OC().cust_id);
		_swdoc.EF().sl_cust_incidents.Refresh();
	}
	else if (nTab == tSite)
	{
		app._slf(_swdoc.EF().sl_site_incidents1 , "callref="+_swdoc.OC().callref+"&site="+_swdoc.oCust().site+"&cust_id="+_swdoc.oCust().keysearch);
		_swdoc.EF().sl_site_incidents1.Refresh();
	}
	else if (nTab == tOrganisation)
	{
		app._slf(_swdoc.EF().sl_org_incidents1 , "callref="+_swdoc.OC().callref+"&fk_company_id="+_swdoc.oCust().fk_company_id+"&cust_id="+_swdoc.oCust().keysearch);
		_swdoc.EF().sl_org_incidents1.Refresh();
	}
	else if(nTab==tRelateds)
	{
		if (sl_relincs.rowCount() < 1)sl_relincs.Refresh();
	}	
}

						mbtn_inc_views_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	
	if(nItemID == iDetails)
	{
		if(sl_relincs.curSel > -1)sl_relincs.EditRecord(sl_relincs.curSel);
	}
}

						mbtn_inc_views4_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	
	if(nItemID == iDetails)
	{
		if(sl_majinc.curSel > -1)sl_majinc.EditRecord(sl_majinc.curSel);
	}
}

						mbtn_inc_views5_OnMenuItem(strName, nItemID)
{
	var strKeyValue = app.g.sl_getcolvalue(sl_rakb, sl_rakb.curSel, "docref");
	app.g.open_article(strKeyValue);
}

						mbtn_inc_actions4_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	//-- DJH - unlink KB doc
	if(nItemID == iDisassociate)
	{
		
		var strText = "This Incident has been dis-associated with the following KB Documents\n\n";
		var strDocs = app.g.sl_getselected_valuesdel(sl_rakb,0,",");
		if(strDocs!= "")
		{
			//app.oKB.unrelate_calls_from_kb(strDocs,_swdoc.OC().callref);
			app.g.unrelate_call_from_rakb(strDocs,_swdoc.OC().callref)
			sl_rakb.Refresh();
			
			strText += "Knowledgebase Document (" + strDocs + ") dis-associated\n";
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Incident Management","Knowledgebase Document Dis-association");
		}	
	}	
}

						btn_search_inc4_OnPressed()
{
    if(!app.g.use_sw_knowledgebase())
	{
		app.allocatekbdoc(_swdoc._kbidentifier,_swdoc);
		app.g.open_third_party_kb_search(true, "", _swdoc.OC().callref,app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,","), _swdoc._kbidentifier);
	}
   
	return;
}

