							OnFormLoaded(bDataReloaded)
{
	// TODO: Add your event handler code here

	_swdoc.boolInserting = (_swdoc.oCond().pk_cond_id=="");
	if(_swdoc.boolInserting)
	{
		_swdoc.oCond().analystid = app.session.analystId;
		_swdoc.oCond().created_datex = app.global.GetCurrentEpocTime();
		_swdoc.oCond().appcode = app.itsm.get_session_param("dataset");
		_swdoc.UpdateFormFromData();
	}
	
	_swdoc.MF().Tab1.ShowTabItem(1,(!_swdoc.boolInserting));
	_swdoc.MF().Tab1.ShowTabItem(2,(!_swdoc.boolInserting));
	app._evi(_swdoc.MF().fld_creator, (!_swdoc.boolInserting));
	app._evi(_swdoc.MF().fld_created, (!_swdoc.boolInserting));
	app._evi(_swdoc.MF().lbl_creator, (!_swdoc.boolInserting));
	app._evi(_swdoc.MF().lbl_created, (!_swdoc.boolInserting));	
}

							btn_apply_test1_OnPressed()
{
	_swdoc.boolEntry = false;
	if(tb_value1.text != "")
		_swdoc.boolEntry = true;
	if(tb_valueprice.value!="")
		_swdoc.boolEntry = true;
		
	if((lb_op1.text=="")||(lb_test1.text =="")||!_swdoc.boolEntry)
	{
		MessageBox("Please specify some test criteria");
		return;
	}
	
	var boolString=false;
	var strOp = lb_op1.text;
	var strTest = "";
	switch(lb_test1.text.toLowerCase())
	{
		case "call organisation id":
			strTest = "&[new_opencall.fk_company_id]";		
			boolString = true;
			break;
		case "call charge centre":
			strTest = "&[new_opencall.costcenter]";				
			break;
		case "call department id":
			strTest = "&[new_opencall.fk_dept_code]";				
			boolString = true;		
			break;						
		case "call site":
			strTest = "&[new_opencall.site]";				
			boolString = true;		
			break;			
		case "change type":
			strTest = "&[new_opencall.itsm_rfc_type]";				
			boolString = true;		
			break;			
		case "impact":
			strTest = "&[new_opencall.itsm_impact_level]";				
			boolString = true;		
			break;
		case "request price":
			strTest = "&[request_price]";		
			break;
		case "bpm status":
			strTest = "&[new_opencall.bpm_status_id]";
			boolString = true;		
			break;
	}

	
	//--
	//-- get appropriate value
	var strValue = tb_value1.text;	
	var intPrice = tb_valueprice.value;
	if(boolString)
	{
		strTest = "'" + strTest + "'";
		strValue = "'" + strValue + "'";
	}
	else
	{
		strValue = intPrice;
	}

	var strFullTest = strTest + strOp + strValue;
	_swdoc.oCond().h_condition = _swdoc.oCond().h_condition + " " + strFullTest;
	_swdoc.UpdateFormFromData();
	
	app._een(btn_and1,true);
	app._een(btn_or1,true);

}

							btn_new_auth1_OnPressed()
{
	var x = app.g.sqllist_findrow_byvalue(sl_authors, "CUSTMGR", "fk_auth_id");
	var y = app.g.sqllist_findrow_byvalue(sl_authors, "CUSTOMER", "fk_auth_id");
	//DTH: Also need to know if the Authorisation Type indicates "Affected Business Area". This then needs to be added to the URL
	var z = app.g.sqllist_findrow_byvalue(sl_authors, "BUSAREA", "fk_auth_id");
	
	//SG F0112155 Need Service Owner, Technical Owner and CI Owner Auth Types
	var u = app.g.sqllist_findrow_byvalue(sl_authors, "SERVOWN", "fk_auth_id");
	var v = app.g.sqllist_findrow_byvalue(sl_authors, "TECHOWN", "fk_auth_id");
	var w = app.g.sqllist_findrow_byvalue(sl_authors, "CIOWN", "fk_auth_id");
	
	var strURL = "fk_cond_id=" + _swdoc.oCond().pk_cond_id + "&CUSTMGR="+x+ "&CUSTOMER="+y + "&BUSAREA="+z+ "&SERVOWN="+u+ "&TECHOWN="+v + "&CIOWN="+w;
	app.OpenFormForAdd("bpm_cond_auth", "", strURL, true,function(){
		sl_authors.Refresh();
		sl_authors.SetRowSelected(0);
	});
	
}

							btn_del_auth1_OnPressed()
{
	var strConfirm = "Are you sure you want to delete the selected stage authoriser?";
	if(app.g.sl_deldbrow(sl_authors,sl_authors.curSel,0,null,strConfirm))
	{
		sl_authors_OnItemSelected(0);
	}


}

							sl_processes_OnItemDoubleClicked(nRow, nCol)
