							sl_stage_OnItemSelected(nRow)
{
	//-- NWJ
	//-- 27.11.2006
	//-- enable list menus if an item is selected	
	//MessageBox(nRow)
	var boolActive = (sl_stage.rowCount() > 0);
	app._een(btn_prop_stage, boolActive);
	app._een(btn_del_stage, boolActive);
	app._een(btn_copy_cprocess, boolActive);
	
	app._een(btn_up,(nRow>0));
	app._een(btn_down, ((nRow+1) < sl_stage.rowCount()));
	
}

							TabBPM_OnTabSelected(nTab)
{
	var tDetails = 0;
	var tRequest = 1;
	var tStages = 2;
	var tProgress = 3;
	var tAudit = 5;
	if(nTab==4)
	{
		_swdoc.MF().sl_progress.Refresh();
	}
	if(nTab==tDetails)
	{
		if(_swdoc.boolRefreshFirstStage)
			mainform.UpdateFormFromData();
	}
	else if(nTab==tStages)
	{
		_swdoc.boolRefreshFirstStage = true;
		if(_swdoc.MF().sl_stage.rowCount() > 0)
		{
			_swdoc.MF().sl_stage.SetRowSelected(0,true, false);
		}
		else
		{
			sl_stage_OnItemSelected(0);
		}	
		_swdoc.MF().sl_stage.Refresh();	
	}	
	else if (nTab== tProgress)
	{
		if(_swdoc.MF().sl_progress.rowCount() > 0)
		{
			_swdoc.MF().sl_progress.SetRowSelected(0,true, false);
		}
		else
		{
			sl_progress_OnItemSelected(0);
		}
		_swdoc.MF().sl_progress.Refresh();
	}
	else if(nTab == tAudit)
	{
		if(mainform.lb_databinding.enable && !_swdoc.boolAuditLoaded)
		{
			var strExtCols = "";
			var strOCCols = app.g.dd_getcolumnnamelist('opencall',false, false, undefined, true);
			if(bpm_workflow.callclass=="Service Request" || bpm_workflow.callclass=="Change Request" ||bpm_workflow.callclass=="Release Request")
				strExtCols = app.g.dd_getcolumnnamelist('itsm_opencall_rfc', false, false, undefined, true);
			else if(bpm_workflow.callclass=="Problem")
				strExtCols = app.g.dd_getcolumnnamelist('itsm_opencall_problem', false, false, undefined, true);
			if(strExtCols!="")
				strOCCols += "|"+strExtCols;
			var strExcludes = app.g.get_audit_exclusions();
			var arrExcludes = strExcludes.split("|");
			var arrOCCols = strOCCols.split("|");
			strOCCols = "";
			for(x in arrOCCols)
			{
				var thisValue = arrOCCols[x];
				if(!app.in_array(arrExcludes,thisValue))
				{
					if(strOCCols!="")strOCCols+="|";
					strOCCols+=thisValue;
				}
			}
			mainform.lb_databinding.pickList = strOCCols;
			_swdoc.boolAuditLoaded = true;
		}
		app._slf(_swdoc.MF().sl_audit_flds , "swc=fk_workflow_id&kvs="+bpm_workflow.pk_workflow_id);
		_swdoc.MF().sl_audit_flds.Refresh();
	}
	else if(nTab == tRequest)
	{
		_swdoc.MF().sl_fldmgt.Refresh();
		if(_swdoc.WF().callclass == "Service Request")
		{
			// -- Where the callclass is "Service Request", we would want Service Level options to be disabled as Service Requests are driven by Service rather than SLA
			app._een(_swdoc.MF().fld_sla_apply,false);
			app._een(_swdoc.MF().fld_override1,false);
			app._een(_swdoc.MF().fld_requireci,false);
			app._een(_swdoc.MF().bpm_new_sla,false);
			app._een(_swdoc.MF().fld_def_sla,false);
		}
	}	
}

							btn_new_stage_OnPressed()
{
	var intCurrCount = sl_stage.rowCount();
	intCurrCount++;
	//-- create a new stage for current workflow
	var strURL = "fk_workflow_id=" + _swdoc.WF().pk_workflow_id + "&seq=" + intCurrCount + "&callclass=" + _swdoc.WF().callclass;
	app.OpenFormForAdd("bpm_stage", "", strURL, true, function(){
		//-- refresh
		sl_stage.Refresh();
	});
}

							btn_prop_stage_OnPressed()
{
	// F0105843 Start
	if (sl_stage.curSel < 0)
	{
		MessageBox("Please select a Stage to view");
		return;
	}
	var strURL = "callclass=" + _swdoc.WF().callclass;
	app.OpenFormForEdit("bpm_stage", _swdoc.MF().sl_stage.GetItemTextRaw(sl_stage.curSel, "pk_stage_id") , strURL, true,function(){
		sl_stage.Refresh();
	});
	//sl_stage.EditRecord(sl_stage.curSel); // No longer using this method as we can't pass in the call class
	// F0105843 End

}

							btn_del_stage_OnPressed()
{
	//-- delete the selected stage	
	var strConfirm = "Are you sure you want to delete the select process stage?";
	var intStageId = sl_stage.GetItemTextRaw(sl_stage.curSel, 'pk_stage_id');
	var delSequence = sl_stage.GetItemTextRaw(sl_stage.curSel, "seq");
	if( (intStageId!="") && (confirm(strConfirm)) )
	{
		var xmlmc = new XmlMethodCall;
		
		// Set up our input parameters
		xmlmc.SetValue("in_stageid", intStageId);
		
		// Invoke the method
		if(xmlmc.Invoke("VPME", "bpmDeleteStage"))
		{
			MessageBox(xmlmc.GetReturnValue("res1"));
		}
		else
		{
			MessageBox(xmlmc.GetLastError());
		}	
		
		sl_stage.Refresh();
		
		//-- loop through list and set seq for each to #
		var intTask=0;
		for(var x=0; x < sl_stage.rowCount();x++)
		{
			var thisSeq = sl_stage.GetItemTextRaw(x, "seq");
			if(thisSeq>delSequence)
			{
				intStage = sl_stage.GetItemTextRaw(x, "pk_stage_id");
				var strParams = "sid="+app.pfu(intStage);
				app.g.submitsqs("form/bpm_workflow/dec_stage_seq", strParams);
			}
		}

		sl_stage.Refresh();		
		sl_stage.SetRowSelected(0, true, false);					
	}
		
}

							OnFormLoaded()
{
	
	//-- call to set any rec values based on args
	if(bpm_workflow.pk_workflow_id=="")
	{
		app.g.popuplate_form_fromargs("bpm_workflow", _swdoc, false,true);
	}
		
	//-- enable add tasks and progress when updating
	app._een(btn_new_progress,(bpm_workflow.pk_workflow_id!=""));
	app._een(btn_new_stage, (bpm_workflow.pk_workflow_id!=""));
	app._een(btn_new_fldmgt, (bpm_workflow.pk_workflow_id!=""));
	app._ema(lb_firststage,(bpm_workflow.pk_workflow_id!=""));
	app._een(fld_active, (bpm_workflow.fk_firststage_id>0));
	app._een(lb_databinding,(bpm_workflow.pk_workflow_id!=""));
	
	//-- get list of database tables
	var strTables = "|";
	var arrTables = dd.tables;
	for(var x=0;x<arrTables.length;x++)
	{
		strTables +=arrTables[x].name;
		strTables +="|";
	}
	
	lb_datatable.pickList = strTables;
	app._ete(lb_datatable, bpm_workflow.ext_db_table);

	if(bpm_workflow.fk_default_sla_new>999999999)
	{
		var strValue = bpm_workflow.fk_default_sla_name;
		var strPriority = strValue.replace(" SLA]", " Priority]");
		mainform.fld_def_sla1.pickList = strPriority;
		app._evi(mainform.fld_def_sla1, true);
		app._evi(mainform.fld_def_sla, false);	
	}		
	if(bpm_workflow.flg_sla_enforce==1)
	{
		app._een(mainform.fld_sla_apply,false);
	}


	//-- nwj - 16.05.2013 - if opened from bpmui then only show the tab items it as specified in bpmui/js/actions.js hsl: links
	var frombpmui = _swdoc.GetArg("bpmui");	
	if(frombpmui=="1")
	{
		var strHideTabs = _swdoc.GetArg("hidetabs");
		var arrTabs = strHideTabs.split(",");
		for(var x=0;x<arrTabs.length;x++)
		{
			_swdoc.MF().TabBPM.ShowTabItem(arrTabs[x],false);
		}
		app._etab(_swdoc.MF().TabBPM,0);
	}


}

							btn_up_OnPressed()
{
	//-- NWJ : move stage seq up 
	//-- get selected stage id to move up
	var currRow = sl_stage.curSel;
	var intUpStageID = sl_stage.GetItemTextRaw(sl_stage.curSel, 'pk_stage_id');
	var intUpStageSeq = sl_stage.GetItemTextRaw(sl_stage.curSel, 'seq');	
	
	//-- get stage id to move down
	var intDownStageID = sl_stage.GetItemTextRaw(sl_stage.curSel-1, 'pk_stage_id');
	var intDownStageSeq = sl_stage.GetItemTextRaw(sl_stage.curSel-1, 'seq');
	
	//-- update seq id for both stages
	var strParams = "sid="+app.pfu(intUpStageID);
	if(app.g.submitsqs("form/bpm_workflow/dec_stage_seq", strParams))
	{
		var strParams = "sid="+app.pfu(intDownStageID);
		if(app.g.submitsqs("form/bpm_workflow/inc_stage_seq", strParams))
		{
			//-- refresh list
			sl_stage.Refresh();
			sl_stage.SetRowSelected(currRow-1, true, false);
		}
	}

}

