							btn_del_progress_OnPressed()
{
	//-- delete the selected stage	
	var strConfirm = "Are you sure you want to delete the selected progress item?";
	var delSequence = sl_progress.GetItemTextRaw(sl_progress.curSel, "pindex");
	if(app.g.sl_deldbrow(sl_progress,sl_progress.curSel,0,null,strConfirm))
	{
			for(var x=0; x < sl_progress.rowCount(); x++)
			{
				var thisSeq = sl_progress.GetItemTextRaw(x, "pindex");
				if(thisSeq>delSequence)
				{
					var id = sl_progress.GetItemTextRaw(x, "pk_progid");
					var strParams = "sid="+app.pfu(id);
					app.g.submitsqs("form/bpm_workflow/dec_progress_seq", strParams);
				}
			}
			sl_progress.Refresh();
			sl_progress.SetRowSelected(0, true, false);
	}


}

