							bpm_new_sla_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here

	bpm_workflow.fk_default_sla = "";
	if(strValue=="")
	{
		bpm_workflow.fk_default_sla_new = 0;
		app._evi(mainform.fld_def_sla, true);
		app._evi(mainform.fld_def_sla1, false);			
	}
	else
	{
 		var oRec = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strValue);
		if(oRec.Fetch())
		{
			app._evi(mainform.fld_def_sla, true);
			app._evi(mainform.fld_def_sla1, false);			
			bpm_workflow.fk_default_sla_new = app.g.get_field(oRec, "pk_slad_id");
		}
		else
		{
			bpm_workflow.fk_default_sla_new = 1000000000;
			var strPriority = strValue.replace(" SLA]", " Priority]");
			mainform.fld_def_sla1.pickList = strPriority;
			app._evi(mainform.fld_def_sla1, true);
			app._evi(mainform.fld_def_sla, false);			
		}
	
	}
	_swdoc.UpdateFormFromData();
	
}

