							btn_new_audit_OnPressed()
{
	var strValue = mainform.lb_databinding.value;
	var intRow = app.g.sqllist_findrow_byvalue(_swdoc.MF().sl_audit_flds, strValue, 'fieldname');
	if(intRow==-1)
	{
		var oInsertTmp = new app.oXmlmcInsert("bpm_audit_fields");
		var strWorkflow = _swdoc.bpm_workflow.pk_workflow_id;
		oInsertTmp.setfield("fk_workflow_id", strWorkflow);	
		oInsertTmp.setfield("fieldname", strValue);
		if(oInsertTmp.execute(false))
		{
			_swdoc.MF().sl_audit_flds.Refresh();
		}
		else
		{
			MessageBox('Failed to insert audit entry record.');
		}
	}
	else
	{
		MessageBox('Selected field already exists in field list for audit.');
	}
					
}

