OnValidate()
{
	var intExpiresOn = _swdoc.oOLA().expiresonx;
	var intValidFrom = _swdoc.oOLA().validfromx;
	var strWarning = "";

	if(intExpiresOn < intValidFrom)
	{
		strWarning = "Expires on date cannot be before valid from date.";
	}

	if(strWarning!="")
	{

		MessageBox(strWarning,16);
		return VALIDATE_FAIL;
	}

	return VALIDATE_DEFAULT;
}

