mbtn_ci_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	


	if(nItemID == iAssignCI)
	{
		//-- F0091281
		if(!_swdoc.check_edit_rights())
			return false;
		var strURL = "fk_company_id=";// + oCust().fk_company_id;
		app.cmdb.search_ci(true,strURL,function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			var arrKeys = sResults.selectedkeys.split(",");
			for (var x=0; x< arrKeys.length;x++)
			{
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_results1,fk_key,"pk_auto_id")==-1)
				{
         			var strRel = "Uses";
					app.cmdb.generate_meconfigrelation(itsmsp_slad_ola.fk_cmdb_id,fk_key,strRel,"No",function()
					{
						app.g.sqllist_appendfilter(sl_results1, fk_key, "pk_auto_id");
						_swdoc.MF().sl_results1.Refresh();
						return;
					});  

				}
				else
				{
					_swdoc.MF().sl_results1.Refresh();
					return;
				}	
			}
		});

    }
	else if(nItemID == iRemoveCI)
	{
		//-- F0091281
		if(!_swdoc.check_edit_rights())
			return false;
		//-- remove customer and ci relation
		var strCIids = app.g.sl_selectedkeys(sl_results1,'pk_auto_id',true,",");
		if(strCIids=="")  return;

		if(!confirm("Are you sure you want to dis-associate the selected Configuration Items(s) from this Operational Level Agreement?"))return;

		var arrKeys = strCIids.split(",");
		for (var x=0; x< arrKeys.length;x++)
		{
			var fk_key = arrKeys[x];
			app.cmdb.delete_meconfigrelation(itsmsp_slad_ola.fk_cmdb_id,fk_key);
		}

		var strCIs = app.g.get_ola_cis(itsmsp_slad_ola.pk_ola_id);
		app._slf(sl_results1,"pids="+strCIs);
		_swdoc.MF().sl_results1.Refresh();

	}





}

