sl_priorities1_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here

	app._een(btn_remove_priority1, (sl_priorities1.curSel>-1));

	var strSLAName = app.g.sl_getcolvalue(sl_priorities1, nRow, "fk_priority");
	if(strSLAName!="")
	{
		var oSLA = false;
		var oRS = app.g.get_sqrecordset("call/common/get_priority_details", "nm="+app.pfu(strSLAName));
		if(oRS.Fetch())
		{
			var intCols = oRS.GetColumnCount();

			oSLA = new Object();
			for (x=0; x < intCols;x++)
			{
				var colName = oRS.GetColumnName(x);
				var colValue = app.g.get_field(oRS,x);
				oSLA[app.LC(colName)] = colValue;
			}
		}
		if(oSLA==false)
		{
			if(strSLAName!="")
				MessageBox("Unable to load Priority details. Please contact your System Administrator.");
			oSLA = new Object();
			oSLA["fixhrs"] = "";
			oSLA["fixmins"] = "";
			oSLA["resphrs"] = "";
			oSLA["respmins"] = "";
			oSLA["timezone"] = "";
			app._een(mainform.btn_edit_priority, false);
		}else
		{
			var lclRespTime = oSLA.resptime/60;
			var lclRespMins = lclRespTime % 60;
			var lclRespHrs = (lclRespTime - lclRespMins) / 60;
			oSLA["resphrs"] = lclRespHrs;
			oSLA["respmins"] = lclRespMins;

			var lclFixTime = oSLA.fixtime/60;
			var lclFixMins = lclFixTime % 60;
			var lclFixHrs = (lclFixTime - lclFixMins) / 60;
			oSLA["fixhrs"] = lclFixHrs;
			oSLA["fixmins"] = lclFixMins;
			app._een(mainform.btn_edit_priority, true);
		}
	}
	else
	{
		oSLA = new Object();
		oSLA["fixhrs"] = "";
		oSLA["fixmins"] = "";
		oSLA["resphrs"] = "";
