	}else if(strFilter.indexOf("service_type like '500%' OR")>-1)
	{
		strFilter = strFilter.replace("service_type like '500%' OR", "");
	}else if(strFilter.indexOf("service_type like '500%'")>-1)
	{
		strFilter = strFilter.replace("service_type like '500%'", "");
	}
	*/
	
	//always make sure they are app.service catalog items being shown
	if(strFilter!="")strFilter += "&";
