							mbtn_view_ci_OnMenuItem(strName, nItemID)
{
  	var iProperties = 1;
	var iVCMp = 5;	
	var iVCMc = 2;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(_swdoc.currentList,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(_swdoc.currentList,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
		}
		else if(nItemID == iVCMc)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iVCMp)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

							TabRes_OnTabSelected(nTab)
{
	if(nTab==0)
	{
		app._een(mbtn_view_ci, (app.g.sl_getselected_count(sl_results)==1));
		_swdoc.currentList = sl_results;	
	}
	else if (nTab==1)
	{
		app._een(mbtn_view_ci, (app.g.sl_getselected_count(sl_sub)==1));
		_swdoc.currentList = sl_sub;
	}
		
}

