							sl_cust_services1_OnItemChecked(nRow, bChecked)
{
	//-- get selected ci id
	var pk_ci_id = app.g.sl_getrawcolvalue(sl_cust_services1, nRow, "pk_auto_id");	
	
	if(bChecked)
	{
	 
		if(app.g.sl_findrow_byvalue(EF().sl_configitems, pk_ci_id, 'pk_auto_id') > -1) return;
	
		app.g.sl_appendfilter(EF().sl_configitems, pk_ci_id,'pk_auto_id');

		//-- if only 1 CI use it default profile unless blank
		//if(EF().sl_configitems.rowCount()==1)
		//{
		//	var strCode = app.g.sl_getrawcolvalue(EF().sl_configitems,0,"fk_profilecode");
		//	if(strCode!="") 
		//	{
		//		OC().probcode = strCode;
		//		_swdoc.UpdateFormFromData();		
		//	}
		//}
		
		//-- NWJ - determine who the preferred support is
 		_swdoc.determine_prefered_support(true);
 		
 		//-- nwj - 15.07.2008 - check ci contract
		app.cfg.process_call_contract_check(pk_ci_id, true,_swdoc);
 		
	}
	else
	{
		var intRowPos = app.g.sl_findrow_byvalue(EF().sl_configitems, pk_ci_id, 'pk_auto_id');
		if(intRowPos>-1)
		{
			EF().sl_configitems.RemoveRow(intRowPos);		
			//_swdoc.check_ci_assessment();
				//-- 29.04.2008 - 2.2.0 if this ci that has been removed was used to set the pref supp group then change it
				if(_swdoc.intAssessmentCI == pk_ci_id)
				{
					OC().itsm_prefered_suppgroup = "";
					OC().itsm_prefered_text = "";
				}		
				determine_prefered_support(true);

		}

	}



}

